const handleSelectChange = (e) => {
    const { name, value } = e.target;
    if (name === 'S_id1' || name === 'S_id2') {
        setSData(prevState => ({
            ...prevState,
            [name]: value
        }));
    } else if (name === 'T_id') {
        setTData({ T_id: value });
    }
};
