import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import { Stack } from '@mui/system';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';

function ChairmanScoreCSB02() {
    const [projects, setProjects] = useState([]);
    const [selectedProject, setSelectedProject] = useState(null);
    const [data, setData] = useState([{ id: 1, name: 'คะแนนรวม', fullscores: '90', score: '' }]);
    const [editingRowId, setEditingRowId] = useState(null);


    const fetchProjects = async () => {
        try {
            const projectResponse = await axios.get('http://localhost:9999/Project');
            const examResponse = await axios.get('http://localhost:9999/Exam_results');

            const filteredProjects = projectResponse.data.filter(project => {
                const result = examResponse.data.find(result => result.Er_Pname === project.P_name);
                return result?.Er_CSB02 !== ''; 
            });
            setProjects(filteredProjects);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const projectResponse = await axios.get('http://localhost:9999/Project');
                const examResponse = await axios.get('http://localhost:9999/Exam_results');

                const filteredProjects = projectResponse.data.filter(project => {
                    const result = examResponse.data.find(result => result.Er_Pname === project.P_name);
                    return result?.Er_CSB02 !== ''; 
                });
                setProjects(filteredProjects);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchProjects();
    }, []);

    useEffect(() => {
        const fetchExamResults = async () => {
            if (selectedProject) {
                try {
                    const examResponse = await axios.get('http://localhost:9999/Exam_results');
                    const projectExamResult = examResponse.data.find(result => result.Er_Pname === selectedProject.P_name);
                    if (projectExamResult) {
                        setData([{ id: 1, name: 'คะแนนรวม', fullscores: '90', score: projectExamResult.Er_CSB02 }]);
                    } else {
                        setData([{ id: 1, name: 'คะแนนรวม', fullscores: '90', score: '' }]);
                    }
                } catch (error) {
                    console.error('Error fetching exam results:', error);
                }
            }
        };

        fetchExamResults();
    }, [selectedProject]);


    const handleChange = (event) => {
        const selectedProject = projects.find(p => p.P_name === event.target.value);
        setSelectedProject(selectedProject);
    };


    const handleNameChange = (value) => {
        if (/^\d*$/.test(value) && Number(value) <= Number(data[0].fullscores)) {
            setData(prevData => prevData.map(item =>
                item.id === 1 ? { ...item, score: value } : item
            ));
        }
    };    

    const handleEditClick = () => {
        setEditingRowId(1);
    };

    const handleSaveClick = () => {
        setEditingRowId(null);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (!selectedProject?.P_id) {
                alert("Project ID is missing. Please select a project.");
                return;
            }

            const updatedEr_CSB02 = data.find(item => item.name === 'คะแนนรวม')?.score;

            const payload = {
                Er_Pname: selectedProject?.P_name,
                Er_CSB01: '',
                Er_CSB02: updatedEr_CSB02 || '',
                Er_CSB03: '',
                Er_CSB01_status: '',
                Er_CSB02_status: '',
                Er_CSB03_status: ''
            };

            console.log('Updating with data:', payload);

            // Use the selectedProject.P_id for the URL
            const response = await axios.put(`http://localhost:9999/Exam_results/${selectedProject.P_id}`, payload);

            console.log('Server response:', response);

            if (response.status === 200) {
                console.log('Subject updated:', response.data);
                alert("Subject updated successfully!");
            } else {
                console.error('Unexpected response status:', response.status);
                alert("Update failed. Please check the server response.");
            }
        } catch (error) {
            console.error('Error updating subject:', error);
            alert("Failed to update subject. Please try again later.");
        }
    };


    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Grid container direction="column" spacing={1}>
                                <Box fontSize='18px' sx={{ marginTop: 5 }}>
                                    <h1>แบบประเมินโครงงานพิเศษ 1 (สอบก้าวหน้า)</h1>
                                    <p>เลือกชื่อโครงงาน</p>
                                    <FormControl>
                                        <InputLabel id="ProjectName-select-label">ชื่อโครงงาน</InputLabel>
                                        <Select
                                            labelId="ProjectName-select-label"
                                            value={selectedProject?.P_name || ''}
                                            onChange={handleChange}
                                            margin="normal"
                                            sx={{ minWidth: 150 }}
                                        >
                                            {projects.map((p) => (
                                                <MenuItem key={p.P_id} value={p.P_name}>
                                                    {p.P_name}
                                                </MenuItem>
                                            ))}
                                        </Select>
                                    </FormControl>
                                    <br />
                                    <p>โดย</p>
                                    <TextField
                                        label="ชื่อ-สกุลนักศึกษาคนที่ 1"
                                        value={selectedProject?.P_S1 || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                    />
                                    <TextField
                                        label="ชื่อ-สกุลนักศึกษาคนที่ 2"
                                        value={selectedProject?.P_S2 || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                    />
                                    <p>อาจารย์ที่ปรึกษา</p>
                                    <TextField
                                        label="ชื่ออาจารย์ที่ปรึกษา"
                                        value={selectedProject?.P_T || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '40ch' } }}
                                    />
                                </Box>
                                {selectedProject && (
                                    <>
                                        <h2>ตารางคะแนนสำหรับประธานกรรมการสอบ</h2>
                                        <TableContainer component={Paper}>
                                            <Table>
                                                <TableHead>
                                                    <TableRow>
                                                        <TableCell>คะแนนเต็ม</TableCell>
                                                        <TableCell>คะแนนได้</TableCell>
                                                        <TableCell>แก้ไข</TableCell>
                                                    </TableRow>
                                                </TableHead>
                                                <TableBody>
                                                    {data.map((item) => (
                                                        <TableRow key={item.id}>
                                                            <TableCell>{item.fullscores}</TableCell>
                                                            <TableCell>
                                                                {editingRowId === item.id ? (
                                                                    <TextField
                                                                        value={item.score}
                                                                        onChange={(e) => handleNameChange(e.target.value)}
                                                                        inputProps={{
                                                                            inputMode: 'numeric',
                                                                            pattern: '[0-9]*'
                                                                        }}
                                                                    />
                                                                ) : (
                                                                    item.score
                                                                )}
                                                            </TableCell>
                                                            <TableCell>
                                                                {editingRowId === item.id ? (
                                                                    <Button variant="contained" onClick={handleSaveClick}>
                                                                        บันทึก
                                                                    </Button>
                                                                ) : (
                                                                    <Button variant="contained" onClick={handleEditClick}>
                                                                        แก้ไข
                                                                    </Button>
                                                                )}
                                                            </TableCell>
                                                        </TableRow>
                                                    ))}
                                                </TableBody>
                                            </Table>
                                        </TableContainer>
                                    </>
                                )}
                                <div>
                                    <form onSubmit={handleSubmit}>
                                        <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
                                            <Button variant="contained" color="primary" type="submit">
                                                บันทึก
                                            </Button>
                                            <Button variant="contained" color="secondary" onClick={() => setSelectedProject(null)}>
                                                ยกเลิก
                                            </Button>
                                        </Stack>
                                    </form>
                                </div>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
}

