import React, { useState } from 'react';

const SpecialProjectEvaluation = () => {
  const [projectInfo, setProjectInfo] = useState({
    projectName: 'โครงงานพิเศษ A',
    studentName: 'นักศึกษา 1',
    advisorName: 'อาจารย์ B',
  });
  
  const [evaluation, setEvaluation] = useState({
    score: '',
    comments: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEvaluation({ ...evaluation, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // ส่งข้อมูลการประเมินไปยังเซิร์ฟเวอร์
    console.log('Submitting Evaluation:', evaluation);
    alert('การประเมินถูกส่งเรียบร้อยแล้ว');
  };

  return (
    <div className="evaluation-container">
      <h1>ประเมินการสอบโครงงานพิเศษ</h1>
      <div className="project-info">
        <p><strong>ชื่อโครงงาน:</strong> {projectInfo.projectName}</p>
        <p><strong>ชื่อนักศึกษา:</strong> {projectInfo.studentName}</p>
        <p><strong>อาจารย์ที่ปรึกษา:</strong> {projectInfo.advisorName}</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="score">คะแนน:</label>
          <input 
            type="number" 
            id="score" 
            name="score" 
            value={evaluation.score} 
            onChange={handleInputChange} 
            required 
          />
        </div>
        <div>
          <label htmlFor="comments">ความคิดเห็น:</label>
          <textarea 
            id="comments" 
            name="comments" 
            value={evaluation.comments} 
            onChange={handleInputChange} 
            required 
          />
        </div>
        <button type="submit">ยืนยันการส่ง</button>
      </form>
    </div>
  );
};

export default SpecialProjectEvaluation;
