import React, { useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Button, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import { Stack } from '@mui/system';
import FormControlLabel from '@mui/material/FormControlLabel';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';

import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';

function EvaluateProject() {
  const [tableData, setTableData] = useState([
    { id: 1, name: 'จัดการการสอบโครงงานพิเศษ', link: 'path/to/file1.txt', status: 'CSB03', isDisabled: false, isRed: false },
    { id: 2, name: 'จัดการการยื่นจบการศึกษา', link: 'path/to/file2.txt', status: 'CSB03', isDisabled: false, isRed: false },
    { id: 3, name: 'จัดการการใช้บริการฟิตเนส', link: 'path/to/file3.txt', status: 'CSB02', isDisabled: false, isRed: false }
  ]);

  const project = [
    {
      "ID": "001",
      "Name": "ระบบตรวจสอบการวัดพื้นที่",
      "SID": "6304062620061",
      "SName": "ณัชริกา กันทะสอน",
      "SID2": "6304062620062",
      "SName2": "ใจดี ยืมเงิน",
      "TID": "NLP",
      "TName": "ลือพล ไม่น่ารักเลย"
    },
    {
      "ID": "002",
      "Name": "ระบบจำลองโมเดล",
      "SID": "6304062620063",
      "SName": "สบายดี สบายใจ",
      "SID2": "6304062620064",
      "SName2": "สุดสวย สุดหล่อ",
      "TID": "SWK",
      "TName": "สุวัชชัย ตัวตึง"
    },
    {
      "ID": "003",
      "Name": "เว็บไซต์ขายของ",
      "SID": "6304062620065",
      "SName": "ไอ่กล้อง ไอ่อ้วน",
      "SID2": "6304062620066",
      "SName2": "แมวเหมียว น่ารัก",
      "TID": "KAB",
      "TName": "คัณฑารัตน์ สุดละเอียด"
    },
    {
      "ID": "004",
      "Name": "เว็บไซต์ขายที่ดิน",
      "SID": "6304062620067",
      "SName": "มะหมา สุดหล่อ",
      "SID2": "6304062620068",
      "SName2": "หนูน้อย น่ารัก",
      "TID": "CRL",
      "TName": "เฉียบวุฒิ สุดจ้าบ"

    },
    {
      "ID": "005",
      "Name": "เกมส์ปลูกผัก",
      "SID": "6304062620069",
      "SName": "สวัสดีครับ ผมนวย",
      "SID2": "6304062620070",
      "SName2": "ไม่มี ตังค์ค่า",
      "TID": "ARN",
      "TName": "เอิญ ไม่ใจดี"
    }
  ];

  const [openPopup, setOpenPopup] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);

  const handleLinkClick = (index) => {
    setOpenPopup(true);
    setSelectedRow(index);
  };

  const handleSavePopup = () => {
    setOpenPopup(false);
    const updatedData = [...tableData];
    updatedData[selectedRow].isRed = false;
    updatedData[selectedRow].isDisabled = true;
    setTableData(updatedData);
  };

  const handleClosePopup = () => {
    setOpenPopup(false);
  };

  const handleRedRow = (index) => {
    const updatedData = [...tableData];
    updatedData[index].isRed = true;
    updatedData[index].isDisabled = true;
    setTableData(updatedData);
  };

  const handleDisableRow = (index) => {
    const updatedData = [...tableData];
    updatedData[index].isDisabled = true;
    setTableData(updatedData);
  };

  const handleDonNotGoAny = () => {
    const updatedData = tableData.map(row => ({ ...row, isRed: true, isDisabled: true }));
    setTableData(updatedData);
  };

  const [ProjectSelect, setProjectSelect] = useState('');
  const [ProjectSelect2, setProjectSelect2] = useState('');

  const handleChange = (event) => {
    setProjectSelect(project.find(person => person.ID === event.target.value));
  };
  const handleChange2 = (event) => {
    setProjectSelect2(project.find(person => person.ID === event.target.value));
  };

  const [openDialog, setOpenDialog] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const initialData = [
    { id: 1, name: 'วัตถุประสงค์และขอบเขตโครงงาน', fullscores: '10', score: '' },
    { id: 2, name: 'ความเข้าใจระบบงานเดิม/ทฤษฎีหรืองานวิจัย ที่นำมาใช้พัฒนาโครงงาน', fullscores: '20', score: '' },
    { id: 3, name: 'การศึกษาความต้องการของระบบ และการออกแบบ', fullscores: '20', score: '' },
    { id: 4, name: 'การนำเสนอโครงงาน', fullscores: '20', score: '' },
    { id: 5, name: 'รูปแบบรายงาน', fullscores: '10', score: '' },
    { id: 6, name: 'แนวทางการดำเนินงาน', fullscores: '10', score: '' },
    { name: 'คะแนนรวม', fullscores: '90', score: '' }
  ];

  const [data, setData] = useState(initialData);

  const initialData2 = [
    { id: 1, name: 'การออกแบบหรือแนวคิด', fullscores: '10', score: '' },
    { id: 2, name: 'วิธีการ/การดำเนินงาน', fullscores: '20', score: '' },
    { id: 3, name: 'ความสมบูรณ์ของผลงาน', fullscores: '20', score: '' },
    { id: 4, name: 'เนื้อหาและรูปแบบของปริญญานิพนธ์', fullscores: '10', score: '' },
    { id: 5, name: 'การนำเสนอโครงงาน', fullscores: '10', score: '' },
    { id: 6, name: 'การนำผลงานไปใช้ประโยชน์', fullscores: '5', score: '' },
    { id: 7, name: 'สรุป/วิจารณ์/การพัฒนาต่อในอนาคต', fullscores: '5', score: '' },
    { name: 'คะแนนรวม', fullscores: '80', score: '' }
  ];

  const [data2, setData2] = useState(initialData2);

  const calculateTotalScore = (data) => {
    return data.reduce((total, item) => total + (parseInt(item.score) || 0), 0);
  };

  const handleScoreChange = (id, value, data, setData) => {
    const newData = data.map(item => {
      if (item.id === id) {
        return { ...item, score: value };
      }
      return item;
    });
    newData[newData.length - 1].score = calculateTotalScore(newData);
    setData(newData);
  };

  return (
    <MainCard>
      <Grid container spacing={gridSpacing}>
        <Grid item xs={12}>
          <Grid container alignItems="center" justifyContent="space-between" spacing={gridSpacing}>
            <Grid item xs={12}>
              <MainCard title="ตรวจสอบเอกสารโครงงาน">
                <Box sx={{ width: '100%' }}>
                  <TableContainer component={Paper}>
                    <Table aria-label="simple table">
                      <TableHead>
                        <TableRow>
                          <TableCell>ชื่อเอกสาร</TableCell>
                          <TableCell>ลิงค์เอกสาร</TableCell>
                          <TableCell>สถานะการตรวจสอบ</TableCell>
                          <TableCell>ทำเครื่องหมาย</TableCell>
                          <TableCell>ลบ</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {tableData.map((row, index) => (
                          <TableRow key={row.id} style={{ backgroundColor: row.isRed ? 'red' : 'white' }}>
                            <TableCell>{row.name}</TableCell>
                            <TableCell>
                              <a href="#" onClick={() => handleLinkClick(index)}>
                                {row.link}
                              </a>
                            </TableCell>
                            <TableCell>
                              <Select
                                value={row.status}
                                onChange={(event) => {
                                  const updatedData = [...tableData];
                                  updatedData[index].status = event.target.value;
                                  setTableData(updatedData);
                                }}
                                disabled={row.isDisabled}
                              >
                                <MenuItem value="CSB03">CSB03</MenuItem>
                                <MenuItem value="CSB02">CSB02</MenuItem>
                              </Select>
                            </TableCell>
                            <TableCell>
                              <Button variant="contained" onClick={() => handleRedRow(index)} disabled={row.isDisabled}>
                                ทำเครื่องหมายเป็นสีแดง
                              </Button>
                            </TableCell>
                            <TableCell>
                              <Button variant="contained" onClick={() => handleDisableRow(index)} disabled={row.isDisabled}>
                                ลบ
                              </Button>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                  <Button variant="contained" onClick={handleDonNotGoAny}>
                    ทำเครื่องหมายทั้งหมดเป็นสีแดง
                  </Button>
                </Box>
              </MainCard>
            </Grid>
          </Grid>
        </Grid>
        <Grid item xs={12}>
          <Grid container alignItems="center" justifyContent="space-between" spacing={gridSpacing}>
            <Grid item xs={12} sm={6}>
              <MainCard title="ประเมินโครงงาน">
                <form onSubmit={handleSubmit}>
                  <FormControl fullWidth>
                    <InputLabel id="select-project-label">เลือกโครงงาน</InputLabel>
                    <Select
                      labelId="select-project-label"
                      value={ProjectSelect.ID || ''}
                      label="เลือกโครงงาน"
                      onChange={handleChange}
                      required
                    >
                      {project.map((person) => (
                        <MenuItem key={person.ID} value={person.ID}>
                          {person.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                  <TableContainer component={Paper}>
                    <Table aria-label="simple table">
                      <TableHead>
                        <TableRow>
                          <TableCell>หัวข้อการประเมิน</TableCell>
                          <TableCell>คะแนนเต็ม</TableCell>
                          <TableCell>คะแนนที่ได้</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {data.map((row) => (
                          <TableRow key={row.id}>
                            <TableCell>{row.name}</TableCell>
                            <TableCell>{row.fullscores}</TableCell>
                            <TableCell>
                              {row.name === 'คะแนนรวม' ? (
                                <TextField
                                  type="number"
                                  value={row.score}
                                  inputProps={{ readOnly: true }}
                                  fullWidth
                                />
                              ) : (
                                <TextField
                                  type="number"
                                  value={row.score}
                                  onChange={(e) => handleScoreChange(row.id, e.target.value, data, setData)}
                                  fullWidth
                                  inputProps={{ max: row.fullscores, min: 0 }}
                                  required
                                />
                              )}
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                  <Button variant="contained" type="submit">
                    ประเมิน
                  </Button>
                </form>
              </MainCard>
            </Grid>
            <Grid item xs={12} sm={6}>
              <MainCard title="ประเมินวิทยานิพนธ์">
                <form onSubmit={handleSubmit2}>
                  <FormControl fullWidth>
                    <InputLabel id="select-project-label-2">เลือกโครงงาน</InputLabel>
                    <Select
                      labelId="select-project-label-2"
                      value={ProjectSelect2.ID || ''}
                      label="เลือกโครงงาน"
                      onChange={handleChange2}
                      required
                    >
                      {project.map((person) => (
                        <MenuItem key={person.ID} value={person.ID}>
                          {person.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                  <TableContainer component={Paper}>
                    <Table aria-label="simple table">
                      <TableHead>
                        <TableRow>
                          <TableCell>หัวข้อการประเมิน</TableCell>
                          <TableCell>คะแนนเต็ม</TableCell>
                          <TableCell>คะแนนที่ได้</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {data2.map((row) => (
                          <TableRow key={row.id}>
                            <TableCell>{row.name}</TableCell>
                            <TableCell>{row.fullscores}</TableCell>
                            <TableCell>
                              {row.name === 'คะแนนรวม' ? (
                                <TextField
                                  type="number"
                                  value={row.score}
                                  inputProps={{ readOnly: true }}
                                  fullWidth
                                />
                              ) : (
                                <TextField
                                  type="number"
                                  value={row.score}
                                  onChange={(e) => handleScoreChange(row.id, e.target.value, data2, setData2)}
                                  fullWidth
                                  inputProps={{ max: row.fullscores, min: 0 }}
                                  required
                                />
                              )}
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                  <Button variant="contained" type="submit">
                    ประเมิน
                  </Button>
                </form>
              </MainCard>
            </Grid>
          </Grid>
        </Grid>
      </Grid>

      <Dialog open={openPopup} onClose={handleClosePopup}>
        <DialogTitle>ตรวจสอบเอกสาร</DialogTitle>
        <DialogContent>
          <iframe
            src={selectedRow !== null ? tableData[selectedRow].link : ''}
            title="เอกสาร"
            width="100%"
            height="400px"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClosePopup}>ปิด</Button>
          <Button onClick={handleSavePopup} variant="contained">
            บันทึก
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>ผลการประเมิน</DialogTitle>
        <DialogContent>
          <TableContainer component={Paper}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>หัวข้อการประเมิน</TableCell>
                  <TableCell>คะแนนเต็ม</TableCell>
                  <TableCell>คะแนนที่ได้</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {data.map((row) => (
                  <TableRow key={row.id}>
                    <TableCell>{row.name}</TableCell>
                    <TableCell>{row.fullscores}</TableCell>
                    <TableCell>{row.score}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} variant="contained">
            ปิด
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog open={openDialog2} onClose={handleCloseDialog2}>
        <DialogTitle>ผลการประเมิน</DialogTitle>
        <DialogContent>
          <TableContainer component={Paper}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>หัวข้อการประเมิน</TableCell>
                  <TableCell>คะแนนเต็ม</TableCell>
                  <TableCell>คะแนนที่ได้</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {data2.map((row) => (
                  <TableRow key={row.id}>
                    <TableCell>{row.name}</TableCell>
                    <TableCell>{row.fullscores}</TableCell>
                    <TableCell>{row.score}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog2} variant="contained">
            ปิด
          </Button>
        </DialogActions>
      </Dialog>
    </MainCard>
  );
}

export default EvaluateProject;
