import React, { useState, useEffect } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography } from '@mui/material';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';

function RemainingStudentsName() {
    const [studentData, setStudentData] = useState([]);
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [studentsResponse, projectsResponse] = await Promise.all([
                    axios.get('http://localhost:9999/students'),
                    axios.get('http://localhost:9999/Project')
                ]);

                if (studentsResponse.data && Array.isArray(studentsResponse.data)) {
                    const uniqueStudents = Array.from(new Map(studentsResponse.data.map(item => [item.S_id, item])).values());
                    setStudentData(uniqueStudents); // Store all student data
                }

                if (projectsResponse.data && Array.isArray(projectsResponse.data)) {
                    setProjects(projectsResponse.data); // Store project data
                }

            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);


    // Filter students based on S_T_SP1 being 'ผ่าน', S_match being 'แยกคู่โครงงาน', and exclude those with S_T_SP2 as 'ผ่าน'
    const filteredStudentData = studentData.filter(student => {
        return student.S_match === 'แยกคู่โครงงาน'; // Add this condition
    });

    const cRemain = filteredStudentData.map(item => ({
        id: uuidv4(),
        StudentID: item.S_id,
        name: item.S_name,
    }));

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <div>
                                <Typography variant="h1" sx={{ marginLeft: "center" }}>
                                    รายชื่อนักศึกษาโครงการพิเศษสองภาษา
                                </Typography>
                                <TableContainer>
                                    <Table>
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>ลำดับที่</TableCell>
                                                <TableCell>รหัสนักศึกษา</TableCell>
                                                <TableCell>ชื่อ-สกุล</TableCell>
                                                <TableCell>สถานะโครงงาน</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {checklistData.length > 0 ? (
                                                checklistData.map((item, index) => (
                                                    <TableRow key={item.id}>
                                                        <TableCell>{index + 1}</TableCell>
                                                        <TableCell>{item.StudentID}</TableCell>
                                                        <TableCell>{item.name}</TableCell>
                                                        <TableCell>{item.StatusProject}</TableCell>
                                                    </TableRow>
                                                ))
                                            ) : (
                                                <TableRow>
                                                    <TableCell colSpan={4}>No data available</TableCell>
                                                </TableRow>
                                            )}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                            </div>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
}

export default RemainingStudentsName;
