import React, { useState, useEffect } from 'react';
import { Button, Paper, Grid, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TablePagination } from '@mui/material';
import axios from 'axios';
import { gridSpacing } from 'store/constant';

function InputScoreCSB01() {
  const [projects, setProjects] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get('http://localhost:9999/Project');
        setProjects(response.data); // Assuming response.data contains the list of projects
      } catch (error) {
        console.error('Error fetching projects:', error);
      }
    };

    fetchProjects();
  }, []);

  const handleDonNotGoAny = () => {
    // Handle the logic to disable or mark rows
    const updatedData = projects.map(project => ({ ...project, isRed: true, isDisabled: true }));
    setProjects(updatedData);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  return (
    <Paper style={{ padding: 16 }}>
      <Grid container spacing={gridSpacing}>
        <Grid item xs={12}>
          <Grid container direction="column" spacing={1}>
            <h1>ประเมินการสอบโครงงานพิเศษ</h1>
            <Button onClick={handleDonNotGoAny} variant="contained" color="error">
              ไม่เข้าร่วมประเมินทั้งหมด
            </Button>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ลำดับที่</TableCell>
                    <TableCell>ชื่อโครงงาน</TableCell>
                    <TableCell>ประเมินการสอบโครงงานพิเศษ</TableCell>
                    <TableCell>ไม่ประสงค์ลงคะแนนสอบ</TableCell>
                    <TableCell>ไม่เข้าประเมินการสอบ</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {projects.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((project, index) => (
                    <TableRow key={project.P_id}>
                      <TableCell>{index + 1 + page * rowsPerPage}</TableCell>
                      <TableCell>{project.P_name}</TableCell>
                      <TableCell>
                        {/* Your evaluation logic here */}
                      </TableCell>
                      <TableCell>
                        {/* Your logic for "ไม่ประสงค์ลงคะแนนสอบ" here */}
                      </TableCell>
                      <TableCell>
                        {/* Your logic for "ไม่เข้าประเมินการสอบ" here */}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
              <TablePagination
                rowsPerPageOptions={[5, 10, 25]}
                component="div"
                count={projects.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
              />
            </TableContainer>
          </Grid>
        </Grid>
      </Grid>
    </Paper>
  );
}

export default InputScoreCSB01;
