import React, { useState, useEffect } from 'react';
import { Table, Typography, TableBody, TableCell, TableContainer, TableHead, TableRow, Button, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import { Grid } from '@mui/material';
import axios from 'axios';
import { gridSpacing } from 'store/constant';

function InputScoreCSB03() {
  const [Data, setData] = useState([
    { id: 1, name: 'การออกแบบหรือแนวคิด', fullscores: '10', score: '' },
    { id: 2, name: 'วิธีการ/การดำเนินงาน', fullscores: '20', score: '' },
    { id: 3, name: 'ความสมบูรณ์ของผลงาน', fullscores: '20', score: '' },
    { id: 4, name: 'เนื้อหาและรูปแบบของปริญญานิพนธ์', fullscores: '10', score: '' },
    { id: 5, name: 'การนำเสนอโครงงาน', fullscores: '10', score: '' },
    { id: 6, name: 'การนำผลงานไปใช้ประโยชน์', fullscores: '5', score: '' },
    { id: 7, name: 'สรุป/วิจารณ์/การพัฒนาต่อในอนาคต', fullscores: '5', score: '' },
    { id: 8, name: 'คะแนนรวม', fullscores: '80', score: '' }
  ]);
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [openPopup, setOpenPopup] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [allChecked, setAllChecked] = useState(false);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const projectResponse = await axios.get('http://localhost:9999/Project');
        const examResultsResponse = await axios.get('http://localhost:9999/Exam_results');

        // Filter projects with completed scores
        const completedProjectIds = new Set(examResultsResponse.data
          .filter(result => result.Er_CSB01)
          .map(result => result.Er_name));

        const filteredProjects = projectResponse.data
          .filter(project => !completedProjectIds.has(project.P_name));

        setProjects(filteredProjects);
      } catch (error) {
        console.error('Error fetching projects or exam results:', error);
      }
    };

    fetchProjects();
  }, []);

  const handleLinkClick = (index) => {
    setSelectedProject(projects[index]);
    setOpenPopup(true);
  };

  const handleSavePopup = async () => {
    if (allChecked) {
      try {
        // Simulate saving the score
        await saveScore();
        // Remove only the selected project from the list
        setProjects(prevProjects => prevProjects.filter(project => project.P_id !== selectedProject.P_id));
        setOpenPopup(false);
      } catch (error) {
        console.error('Error saving score:', error);
      }
    } else {
      alert('กรุณาตรวจสอบให้ครบทุกหัวข้อก่อนทำการบันทึก');
    }
  };

  const handleClosePopup = () => {
    setOpenPopup(false);
  };

  const handleRedRow = (index) => {
    const updatedProjects = projects.map((project, i) =>
      i === index ? { ...project, isRed: true } : project
    );
    setProjects(updatedProjects);
  };

  const handleDisableRow = (index) => {
    const updatedProjects = projects.map((project, i) =>
      i === index ? { ...project, isDisabled: true } : project
    );
    setProjects(updatedProjects);
  };

  const handleChange5 = (id, field, value) => {
    const isValidInput = (input) => /^[0-9]*$/.test(input);

    setData(prevData => {
      const updatedData = prevData.map(item => {
        if (item.id === id) {
          const numericValue = Number(value);
          if (field === 'score' && (!isValidInput(value) || numericValue < 0 || numericValue > Number(item.fullscores) || isNaN(numericValue))) {
            return item; 
          }
          return { ...item, [field]: value };
        }
        return item;
      });

      const totalScore = updatedData.reduce((acc, item) => {
        if (item.id !== 8) {
          return acc + Number(item.score || 0);
        }
        return acc;
      }, 0);

      return updatedData.map(item => {
        if (item.id === 8) {
          return { ...item, score: totalScore.toString() };
        }
        return item;
      });
    });
  };

  return (
