import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Stack } from '@mui/system';
import { Typography } from '@mui/material';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import FormGroup from '@mui/material/FormGroup';
import Button from '@mui/material/Button';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Dialog from '@mui/material/Dialog';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';
import axios from 'axios';

function CSB01() {
    const [StudentSelect, setStudentSelect] = useState('');
    const [StudentSelect2, setStudentSelect2] = useState('');
    const [selectTeacher, setSelectTeacher] = useState('');

    const [selectedValues, setSelectedValues] = useState({
        networks: false,
        graphics: false,
        database: false,
        ai: false,
        games: false,
    });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setSData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleChange2 = (event) => {
        setStudentSelect2(Students.find(person => person.ID === event.target.value));
    };

    const handleSelectTeacherChange = (event) => {
        // const value = event.target.value;
        setSelectTeacher(Teachers.find(person => person.ID === event.target.value));
        console.log(Teachers.find(person => person.ID === event.target.value)); // Log the selected teacher ID
    };

    const handleChangeSelectObject = (event) => {
        const { name, checked } = event.target;

        // Update the state of the corresponding checkbox
        setSelectedValues((prevValues) => ({
            ...prevValues,
            [name]: checked,
        }));

        // Log the updated state
        console.log(`${name} is now ${checked ? 'selected' : 'deselected'}`);
    };

    const [openDialog, setOpenDialog] = useState(false);

    const handleCloseDialog = () => {
        setOpenDialog(false);
    };

    const [selectedValue, setSelectedValue] = useState('');

    const handleRadioChange = (e) => {
        setSelectedValue(e.target.value);
    };

    const handleProjectNameChange = (e) => {
        // Regular expression to allow only English letters and spaces
        const regex = /^[A-Za-z\s]*$/;

        if (regex.test(e.target.value)) {
            setPData({ ...PData, P_name: e.target.value });
        }
    };


    const handleSelectChange = (e) => {
        const { name, value } = e.target;
        if (name === 'S_id1' || name === 'S_id2') {
            setSData(prevState => ({
                ...prevState,
                [name]: value
            }));
        } else if (name === 'T_id') {
            setTData({ T_id: value });
        }
    };

    const [studentData, setStudentData] = useState([]); // เก็บข้อมูลทั้งหมดของนักเรียน (แก้ไขเป็น studentData)
    const [studentNames, setStudentNames] = useState([]); // เก็บเฉพาะรายชื่อนักเรียนสำหรับ Select (แก้ไขเป็น studentNames)
    const [teachers, setTeachers] = useState([]);

    const [PData, setPData] = useState({
        P_name: '',
        P_details: '',
        P_status: '',
        P_CSB01: '',
        P_CSB02: '',
        P_CSB03: '',
        P_S1: '',
        P_S2: '',
        P_T: '',
        P_type: '',
        P_tool: '',
    });

    const [SData, setSData] = useState({
        S_id: '',
        S_name: '',
        S_email: '',
        S_phone: '',
        S_project: '',
        S_CSB01: '',
        S_CSB02: '',
        S_CSB03: '',
        S_year: '',
        S_match: '',
        S_T_SP1: '',
        S_T_SP2: ''

    });

    const [TData, setTData] = useState({
        T_id: '',
        T_name: '',
        T_email: '',
        T_phone: '',
        T_position: '',
        T_CSB01: '',
        T_CSB02: '',
        T_CSB03: '',
        T_CSB01_point: '',
        T_CSB02_point: '',
        T_CSB03_point: '',

    });

    useEffect(() => {
        const fetchStudent = async () => {
            try {
                const response = await axios.get('http://localhost:9999/students');
                if (response.data && Array.isArray(response.data)) {
                    // กรองข้อมูลซ้ำ
                    const uniqueStudents = Array.from(new Map(response.data.map(item => [item.S_id, item])).values());
                    setStudentData(uniqueStudents); // เก็บข้อมูลทั้งหมดของนักเรียน
                    setStudentNames(uniqueStudents.map(item => ({ S_id: item.S_id, S_name: item.S_name }))); // เก็บเฉพาะ S_id และ S_name
                }
            } catch (error) {
                console.error('Error fetching Student names:', error);
            }
        };
        fetchStudent();
    }, []);

    useEffect(() => {
        const fetchTeachers = async () => {
            try {
                const response = await axios.get('http://localhost:9999/Teacher');
                if (response.data && Array.isArray(response.data)) {
                    setTeachers(response.data);
                }
            } catch (error) {
                console.error('Error fetching Teacher names:', error);
            }
        };
        fetchTeachers();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        const student1Name = studentData.find(student => student.S_id === SData.S_id1)?.S_name || '';
        const student2Name = studentData.find(student => student.S_id === SData.S_id2)?.S_name || '';
        const teacherName = teachers.find(teacher => teacher.T_id === TData.T_id)?.T_name || '';
    
        const projectData = {
            ...PData,
            P_S1: student1Name,
            P_S2: student2Name,
            P_T: teacherName
        };
    
        try {
            console.log('Submitting data:', projectData); // Add this line to inspect the data
            const response = await axios.post('http://localhost:9999/Project', projectData);
            console.log('Project added:', response.data);
            alert("Project added successfully!");
            setPData({
                P_name: '',
                P_details: '',
                P_status: '',
                P_CSB01: '',
                P_CSB02: '',
                P_CSB03: '',
                P_S1: '',
                P_S2: '',
                P_T: '',
                P_type: '',
                P_tool: ''
            });
            setSData({
                S_id1: '',
                S_id2: ''
            });
            setTData({
                T_id: ''
            });
        } catch (error) {
            console.error('Error adding project:', error);
            alert("Failed to add project. Please try again later.");
        }
    
        setOpenDialog(true);
    };
    
    const [errors, setErrors] = useState({
        projectName: false,
        projectDetails: false,
        atLeastOneCategory: false
    });

    const getFilteredOptions = (excludedIds) => {
        return studentNames.filter(student => !excludedIds.includes(student.S_id));
    };

    const selectedIds = [SData.S_id1, SData.S_id2].filter(id => id);


    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Grid container direction="column" spacing={1}></Grid>
                            <Box
                                component="form"
                                sx={{
                                    marginTop: 8,
                                    display: 'block',
                                    flexDirection: 'column',
                                }}
                                noValidate
                                autoComplete="off"
                            >
                                <Grid container alignItems="center" justifyContent="center" sx={{ ml: 20 }}>
                                    <Grid item>
                                        <Stack alignItems="center" justifyContent="center" spacing={1}>
                                            <Typography fontSize='18px'>
                                                <b>แบบฟอร์มเสนอหัวข้อโครงงานพิเศษ</b><br></br>
                                            </Typography>
                                            <Typography fontSize='18px'>
                                                <b>โครงการพิเศษ (สองภาษา) ภาควิชาวิทยาการคอมพิวเตอร์และสารสนเทศ</b><br></br>
                                            </Typography>
                                            <Typography fontSize='18px'>
                                                <b>คณะวิทยาศาสตร์ประยุกต์มหาวิทยาลัยเทคโนโลยีพระจอมเกล้าพระนครเหนือ</b>
                                            </Typography>
                                        </Stack>
                                    </Grid>
                                </Grid>
                                <Grid>
                                    <Stack alignItems="center" justifyContent="center" spacing={1} sx={{ mt: 5, ml: 35 }}>
                                        <Typography fontSize='18px'>
                                            รหัสนักศึกษา คนที่ 1
                                            <Box
                                                sx={{
                                                    minWidth: 180,
                                                    marginTop: 1,
                                                    marginLeft: 3,
                                                    display: "flex",
                                                }}
                                            >
                                                <FormControl
                                                    sx={{
                                                        minWidth: 180,
                                                        marginRight: 33,
                                                    }}>
                                                    <InputLabel id="StudentID-select-label-1">รหัสนักศึกษา</InputLabel>
                                                    <Select
                                                        labelId="StudentID-select-label-1"
                                                        id="StudentID-select1"
                                                        name="S_id1"
                                                        value={SData.S_id1}
                                                        label="รหัสนักศึกษา"
                                                        onChange={handleSelectChange}
                                                    >
                                                        {getFilteredOptions([SData.S_id2]).map((student, index) => (
                                                            <MenuItem key={index} value={student.S_id}>
                                                                {student.S_id}
                                                            </MenuItem>
                                                        ))}
                                                    </Select>
                                                </FormControl>
                                                <TextField
                                                    disabled
                                                    id="Name1"
                                                    label="ชื่อ - สกุล"
                                                    value={studentData.find(student => student.S_id === SData.S_id1)?.S_name || ''}
                                                />
                                            </Box>
                                        </Typography>

                                        <Typography
                                            fontSize='18px'
                                            sx={{
                                                marginTop: 5,
                                                marginLeft: 50,
                                            }}>
                                            รหัสนักศึกษา คนที่ 2
                                            <Box
                                                sx={{
                                                    minWidth: 180,
                                                    marginTop: 1,
                                                    marginLeft: 3,
                                                    display: "flex",
                                                }}
                                            >
                                                <FormControl
                                                    sx={{
                                                        minWidth: 180,
                                                        marginRight: 33,
                                                    }}>
                                                    <InputLabel id="StudentID-select-label-2">รหัสนักศึกษา</InputLabel>
                                                    <Select
                                                        labelId="StudentID-select-label-2"
                                                        id="StudentID-select2"
                                                        name="S_id2"
                                                        value={SData.S_id2}
                                                        label="รหัสนักศึกษา"
                                                        onChange={handleSelectChange}
                                                    >
                                                        {getFilteredOptions([SData.S_id1]).map((student, index) => (
                                                            <MenuItem key={index} value={student.S_id}>
                                                                {student.S_id}
                                                            </MenuItem>
                                                        ))}
                                                    </Select>
                                                </FormControl>
                                                <TextField
                                                    disabled
                                                    id="Name2"
                                                    label="ชื่อ - สกุล"
                                                    value={studentData.find(student => student.S_id === SData.S_id2)?.S_name || ''}
                                                />
                                            </Box>
                                        </Typography>

                                    </Stack>
                                </Grid>
                                <div>
                                    <Typography
                                        fontSize='18px'
                                        sx={{
                                            marginTop: 5,
                                            marginLeft: 50,

                                        }}>
                                        ชื่อโครงงานภาษาอังกฤษ
                                        <Box
                                            sx={{
                                                '& .MuiTextField-root': { width: '75ch' },
                                                marginTop: 1,
                                                marginLeft: 3,
                                                display: "flex",
                                            }}
                                        >
                                            <TextField
                                                required
                                                label="ชื่อโครงงานภาษาอังกฤษ"
                                                id="P_name"
                                                value={PData.P_name}
                                                onChange={handleProjectNameChange}
                                                error={errors.projectName}
                                                helperText={errors.projectName ? 'ชื่อโครงงานภาษาอังกฤษจำเป็นต้องกรอก' : ''}
                                                inputProps={{ pattern: "[A-Za-z ]*" }}
                                            />
                                        </Box>
                                    </Typography>
                                </div>
                                <div>
                                    <Typography
                                        fontSize='18px'
                                        sx={{
                                            marginTop: 5,
                                            marginLeft: 50,
                                        }}
                                    >
                                        มีอาจารย์ที่ปรึกษาหรือไม่ ?
                                    </Typography>
                                    <FormControl
                                        fontSize='18px'
                                        sx={{
                                            marginTop: 0,
                                            marginLeft: 55,
                                        }}
                                    >
                                        <RadioGroup
                                            row
                                            aria-labelledby="demo-row-radio-buttons-group-label"
                                            name="row-radio-buttons-group"
                                            id="selectHaveOrDon't"
                                            value={selectedValue}
                                            onChange={handleRadioChange}
                                        >
                                            <FormControlLabel value="Yes" control={<Radio />} label="มี" />
                                            <FormControlLabel value="No" control={<Radio />} label="ไม่มี" sx={{ marginLeft: 10 }} />
                                        </RadioGroup>
                                    </FormControl><br></br>

                                    {selectedValue === 'Yes' && (
                                        <FormControl
                                            sx={{
                                                marginTop: 2,
                                                marginLeft: 55,
                                                minWidth: 180,
                                                marginRight: 33,
                                            }}>
                                            <InputLabel id="teacher-select-label">Teacher</InputLabel>
                                            <Select
                                                labelId="teacher-select-label"
                                                id="teacher-select"
                                                name="T_id"
                                                value={TData.T_id}
                                                label="Teacher"
                                                onChange={handleSelectChange}
                                            >
                                                {teachers.map((teacher) => (
                                                    <MenuItem key={teacher.T_id} value={teacher.T_id}>
                                                        {teacher.T_name}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    )}
                                </div>
                                <div>
                                    <Typography
                                        fontSize='18px'
                                        sx={{
                                            marginTop: 5,
                                            marginLeft: 50,
                                        }}
                                    >
                                        โครงงานนี้จัดอยู่ในประเภท
                                    </Typography>
                                    <FormGroup
                                        id="object-select"
                                        fontSize="18px"
                                        sx={{
                                            marginTop: 0,
                                            marginLeft: 55,
                                        }}
                                    >
                                        {['networks', 'graphics', 'database', 'ai', 'games'].map((category) => (
        <FormControlLabel
            key={category}
            control={
                <Checkbox
                    checked={selectedValues[category]}
                    onChange={handleChangeSelectObject}
                    name={category}
                />
            }
            label={category} // Adjust this label if necessary
        />
    ))}
                                        {errors.atLeastOneCategory && (
                                            <Typography color="error" sx={{ marginLeft: 55, marginTop: 1 }}>
                                                ต้องเลือกประเภทของโครงงานอย่างน้อยหนึ่งรายการ
                                            </Typography>
                                        )}
                                    </FormGroup>
                                </div>
                                <div>
                                    <Typography
                                        fontSize='18px'
                                        sx={{
                                            marginTop: 5,
                                            marginLeft: 50,
                                        }}
                                    >
                                        รายละเอียดของโครงงานที่จะพัฒนา
                                    </Typography>
                                    <Box
                                        sx={{
                                            '& .MuiTextField-root': { width: '85ch' },
                                        }}
                                    >
                                        <TextField
                                            fontSize='18px'
                                            sx={{
                                                marginTop: 0,
                                                marginLeft: 55,
                                            }}
                                            id="details"
                                            label=""
                                            multiline
                                            maxRows={8}
                                            error={errors.projectDetails}
                                            helperText={errors.projectDetails ? 'รายละเอียดของโครงงานต้องกรอก' : ''}
                                            onChange={(e) => setPData({ ...PData, P_details: e.target.value })}
                                        />

                                    </Box>
                                </div>
                                <div>
                                    <Typography
                                        fontSize='18px'
                                        sx={{
                                            marginTop: 5,
                                            marginLeft: 50,
                                        }}
                                    >
                                        เครื่องมือและอุปกรณ์ที่ใช้ในการทำโครงงานนี้
                                    </Typography>
                                    <Box
                                        sx={{
                                            '& .MuiTextField-root': { width: '85ch' },
                                        }}
                                    >
                                        <TextField
                                            fontSize='18px'
                                            sx={{
                                                marginTop: 0,
                                                marginLeft: 55,
                                            }}
                                            id="P_tool"
                                            label=""
                                            multiline
                                            maxRows={8}
                                            value={PData.P_tool}
                                            onChange={(e) => setPData({ ...PData, P_tool: e.target.value })}
                                        />
                                    </Box>
                                </div>
                                <div>
                                    <Stack
                                        alignItems="center"
                                        justifyContent="center"
                                        fontSize='18px'
                                        sx={{
                                            marginTop: 5,
                                            marginLeft: 45
                                        }}
                                    >
                                        <Button variant="contained" onClick={handleSubmit}>
                                            ยืนยัน
                                        </Button>
                                    </Stack>
                                </div>
                                <Dialog open={openDialog} onClose={handleCloseDialog}>
                                    <DialogTitle>ทำการบันทึกสำเร็จ !!</DialogTitle>
                                    <DialogContent>
                                        <p>รอการตรวจสอบจากเจ้าหน้าที่</p>
                                    </DialogContent>
                                    <DialogActions>
                                        <Button onClick={handleCloseDialog}>Close</Button>
                                    </DialogActions>
                                </Dialog>
                            </Box>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
}

export default CSB01;
