                                            <Box
                                                sx={{
                                                    minWidth: 180,
                                                    marginTop: 1,
                                                    marginLeft: 3,
                                                    display: "flex",
                                                }}
                                            >
                                                <FormControl
                                                sx={{
                                                        minWidth: 180,
                                                        marginRight: 33,
                                                    }}>
                                                    <Select
                                                        labelId="StudentID-select-label-1"
                                                        id="StudentID-select"
                                                        value={SData.S_id}
                                                        label="รหัสนักศึกษา"
                                                        onChange={(e) => setSData({ ...SData, S_id: e.target.value })} // อัปเดต S_id เมื่อมีการเปลี่ยนแปลง
                                                    >
                                                        {studentNames.map((student, index) => (
                                                            <MenuItem key={index} value={student.S_id}>
                                                                {student.S_id}
                                                            </MenuItem>
                                                        ))}
                                                    </Select>

                
                                                </FormControl>
                                                <TextField
                                                        disabled
                                                        id="Name1"
                                                        label="ชื่อ - สกุล"
                                                        value={studentData.find(student => student.S_id === SData.S_id)?.S_name || ''}
                                                    />
                                            </Box>
                                        </Typography>
                                        <Typography
                                            fontSize='18px'
                                            sx={{
                                                marginTop: 5,
                                                marginLeft: 50,

                                            }}>
                                            รหัสนักศึกษา คนที่ 2
                                            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
