                                            <Select
                                                id="teacher-select"
                                                value={selectTeacher.ID}
                                                defaultValue=""
                                                displayEmpty
                                                onChange={handleSelectTeacherChange}
                                            >
                                                <MenuItem value="" disabled>
                                                    รายชื่ออาจารย์ที่ปรึกษา
                                                </MenuItem>
                                                {Teachers.map((Teacher) => (
                                                    <MenuItem
                                                        key={Teacher.ID}
                                                        value={Teacher.ID}
                                                    >
                                                        {Teacher.ID + " " + Teacher.Name}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    )}
                                </div>
                                <div>
                                    <Typography
                                        fontSize='18px'
                                        sx={{
                                            marginTop: 5,
                                            marginLeft: 50,
                                        }}
