    const [selectedValues, setSelectedValues] = useState({});
    const [openDialog, setOpenDialog] = useState(false);
    const [errors, setErrors] = useState({});

    const handleSelectChange = (event) => {
        const { name, value } = event.target;
        setSData({ ...SData, [name]: value });
    };

    const handleProjectNameChange = (event) => {
        setPData({ ...PData, P_name: event.target.value });
    };

    const handleRadioChange = (event) => {
        setSelectedValue(event.target.value);
    };

    const handleChangeSelectObject = (event) => {
        const { name, checked } = event.target;
        setSelectedValues({ ...selectedValues, [name]: checked });
    };

    const handleSubmit = () => {
        // เพิ่มตรรกะการตรวจสอบและการส่งข้อมูลที่นี่
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        // รีเซ็ตค่าหรือทำการอื่นๆ ที่ต้องการหลังจากปิด dialog
    };

    const getFilteredOptions = (excludedIds) => {
        // แทนที่ด้วยตรรกะการกรองที่เหมาะสมสำหรับการเลือกนักศึกษา
        return []; // ตัวอย่างการคืนค่าควรจะแก้ไขตามความเหมาะสม
    };

    const teachers = []; // แทนที่ด้วยข้อมูลอาจารย์ที่ถูกต้อง


return (
    <MainCard>
        <Grid container spacing={2}>
            <Grid item xs={12}>
                <Grid container alignItems="center" justifyContent="space-between">
                    <Grid item>
                        <Box
                            component="form"
                            sx={{
                                marginTop: 8,
                                display: 'block',
                                flexDirection: 'column',
                            }}
                            noValidate
                            autoComplete="off"
                        >
                            <Grid container alignItems="center" justifyContent="center">
                                <Grid item>
                                    <Stack alignItems="center" justifyContent="center" spacing={1}>
                                        <Typography fontSize='18px'>
                                            <b>แบบฟอร์มเสนอหัวข้อโครงงานพิเศษ</b><br />
                                        </Typography>
                                        <Typography fontSize='18px'>
                                            <b>โครงการพิเศษ (สองภาษา) ภาควิชาวิทยาการคอมพิวเตอร์และสารสนเทศ</b><br />
                                        </Typography>
                                        <Typography fontSize='18px'>
                                            <b>คณะวิทยาศาสตร์ประยุกต์มหาวิทยาลัยเทคโนโลยีพระจอมเกล้าพระนครเหนือ</b>
                                        </Typography>
                                    </Stack>
                                </Grid>
                            </Grid>

                            {/* Student 1 */}
                            <Typography fontSize='18px'>รหัสนักศึกษา คนที่ 1</Typography>
                            <Box sx={{ display: "flex", alignItems: "center", marginTop: 1 }}>
                                <FormControl sx={{ minWidth: 180, marginRight: 2 }}>
                                    <InputLabel id="StudentID-select-label-1">รหัสนักศึกษา</InputLabel>
                                    <Select
                                        labelId="StudentID-select-label-1"
                                        id="StudentID-select1"
                                        name="S_id1"
                                        value={SData.S_id1}
                                        label="รหัสนักศึกษา"
                                        onChange={handleSelectChange}
                                    >
                                        {getFilteredOptions([SData.S_id2]).map((student, index) => (
                                            <MenuItem key={index} value={student.S_id}>
                                                {student.S_id}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                                <TextField
                                    disabled
                                    label="ชื่อ - สกุล"
                                    value={studentData.find(student => student.S_id === SData.S_id1)?.S_name || ''}
                                />
                            </Box>

                            {/* Student 2 */}
                            <Typography fontSize='18px' sx={{ marginTop: 5 }}>รหัสนักศึกษา คนที่ 2</Typography>
                            <Box sx={{ display: "flex", alignItems: "center", marginTop: 1 }}>
                                <FormControl sx={{ minWidth: 180, marginRight: 2 }}>
                                    <InputLabel id="StudentID-select-label-2">รหัสนักศึกษา</InputLabel>
                                    <Select
                                        labelId="StudentID-select-label-2"
                                        id="StudentID-select2"
                                        name="S_id2"
                                        value={SData.S_id2}
                                        label="รหัสนักศึกษา"
                                        onChange={handleSelectChange}
                                    >
                                        {getFilteredOptions([SData.S_id1]).map((student, index) => (
                                            <MenuItem key={index} value={student.S_id}>
                                                {student.S_id}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                                <TextField
                                    disabled
                                    label="ชื่อ - สกุล"
                                    value={studentData.find(student => student.S_id === SData.S_id2)?.S_name || ''}
                                />
                            </Box>

                            {/* Project Name */}
                            <Typography fontSize='18px' sx={{ marginTop: 5 }}>ชื่อโครงงานภาษาอังกฤษ</Typography>
                            <TextField
                                required
                                label="ชื่อโครงงานภาษาอังกฤษ"
                                id="P_name"
                                value={PData.P_name}
                                onChange={handleProjectNameChange}
                                error={errors.projectName}
                                helperText={errors.projectName ? 'ชื่อโครงงานภาษาอังกฤษจำเป็นต้องกรอก' : ''}
                                inputProps={{ pattern: "[A-Za-z ]*" }}
                                sx={{ marginTop: 1, width: '75%' }}
                            />

                            {/* Advisor */}
                            <Typography fontSize='18px' sx={{ marginTop: 5 }}>มีอาจารย์ที่ปรึกษาหรือไม่?</Typography>
                            <FormControl sx={{ marginTop: 0 }}>
                                <RadioGroup
                                    row
                                    value={selectedValue}
                                    onChange={handleRadioChange}
                                >
                                    <FormControlLabel value="Yes" control={<Radio />} label="มี" />
                                    <FormControlLabel value="No" control={<Radio />} label="ไม่มี" />
                                </RadioGroup>
                            </FormControl>
                            {selectedValue === 'Yes' && (
                                <FormControl sx={{ minWidth: 180, marginTop: 2 }}>
                                    <InputLabel id="teacher-select-label">Teacher</InputLabel>
                                    <Select
                                        labelId="teacher-select-label"
                                        id="teacher-select"
                                        name="T_id"
                                        value={TData.T_id}
                                        label="Teacher"
                                        onChange={handleSelectChange}
                                    >
                                        {teachers.map((teacher) => (
                                            <MenuItem key={teacher.T_id} value={teacher.T_id}>
                                                {teacher.T_name}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                            )}

                            {/* Project Category */}
                            <Typography fontSize='18px' sx={{ marginTop: 5 }}>โครงงานนี้จัดอยู่ในประเภท</Typography>
                            <FormGroup>
                                {['networks', 'graphics', 'database', 'ai', 'games'].map((category) => (
                                    <FormControlLabel
                                        key={category}
                                        control={
                                            <Checkbox
                                                checked={selectedValues[category]}
                                                onChange={handleChangeSelectObject}
                                                name={category}
                                            />
                                        }
                                        label={category}
                                    />
                                ))}
                            </FormGroup>

                            {/* Project Details */}
                            <Typography fontSize='18px' sx={{ marginTop: 5 }}>รายละเอียดของโครงงานที่จะพัฒนา</Typography>
                            <TextField
                                id="details"
                                multiline
                                maxRows={8}
                                sx={{ width: '85%', marginTop: 1 }}
                                onChange={(e) => setPData({ ...PData, P_details: e.target.value })}
                            />

                            {/* Project Tools */}
                            <Typography fontSize='18px' sx={{ marginTop: 5 }}>เครื่องมือและอุปกรณ์ที่ใช้ในการทำโครงงานนี้</Typography>
                            <TextField
                                id="P_tool"
                                multiline
                                maxRows={8}
                                value={PData.P_tool}
                                onChange={(e) => setPData({ ...PData, P_tool: e.target.value })}
                                sx={{ width: '85%', marginTop: 1 }}
                            />

                            {/* Submit Button */}
                            <Stack alignItems="center" justifyContent="center" sx={{ marginTop: 3 }}>
                                <Button variant="contained" onClick={handleSubmit}>
                                    บันทึก
                                </Button>
                            </Stack>
                        </Box>
                    </Grid>
                </Grid>
            </Grid>
        </Grid>

        {/* Dialog for Success Message */}
        <Dialog open={openDialog} onClose={handleCloseDialog}>
            <DialogTitle>บันทึกสำเร็จ</DialogTitle>
            <DialogContent>
                <Typography>ข้อมูลได้ถูกบันทึกเรียบร้อยแล้ว</Typography>
            </DialogContent>
            <DialogActions>
                <Button onClick={handleCloseDialog}>ปิด</Button>
            </DialogActions>
        </Dialog>
    </MainCard>
);
};

export default CSB01;
