import React, { useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Button, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import { Stack } from '@mui/system';
import FormControlLabel from '@mui/material/FormControlLabel';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';

import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';

function EvaluateProject() {
  const [tableData, setTableData] = useState([
    { id: 1, name: 'จัดการการสอบโครงงานพิเศษ', link: 'path/to/file1.txt', status: 'CSB03', isDisabled: false, isRed: false },
    { id: 2, name: 'จัดการการยื่นจบการศึกษา', link: 'path/to/file2.txt', status: 'CSB03', isDisabled: false, isRed: false },
    { id: 3, name: 'จัดการการใช้บริการฟิตเนส', link: 'path/to/file3.txt', status: 'CSB02', isDisabled: false, isRed: false }
  ]);

  const project = [
    {
      "ID": "001",
      "Name": "ระบบตรวจสอบการวัดพื้นที่",
      "SID": "6304062620061",
      "SName": "ณัชริกา กันทะสอน",
      "SID2": "6304062620062",
      "SName2": "ใจดี ยืมเงิน",
      "TID": "NLP",
      "TName": "ลือพล ไม่น่ารักเลย"
    },
    {
      "ID": "002",
      "Name": "ระบบจำลองโมเดล",
      "SID": "6304062620063",
      "SName": "สบายดี สบายใจ",
      "SID2": "6304062620064",
      "SName2": "สุดสวย สุดหล่อ",
      "TID": "SWK",
      "TName": "สุวัชชัย ตัวตึง"
    },
    {
      "ID": "003",
      "Name": "เว็บไซต์ขายของ",
      "SID": "6304062620065",
      "SName": "ไอ่กล้อง ไอ่อ้วน",
      "SID2": "6304062620066",
      "SName2": "แมวเหมียว น่ารัก",
      "TID": "KAB",
      "TName": "คัณฑารัตน์ สุดละเอียด"
    },
    {
      "ID": "004",
      "Name": "เว็บไซต์ขายที่ดิน",
      "SID": "6304062620067",
      "SName": "มะหมา สุดหล่อ",
      "SID2": "6304062620068",
      "SName2": "หนูน้อย น่ารัก",
      "TID": "CRL",
      "TName": "เฉียบวุฒิ สุดจ้าบ"

    },
    {
      "ID": "005",
      "Name": "เกมส์ปลูกผัก",
      "SID": "6304062620069",
      "SName": "สวัสดีครับ ผมนวย",
      "SID2": "6304062620070",
      "SName2": "ไม่มี ตังค์ค่า",
      "TID": "ARN",
      "TName": "เอิญ ไม่ใจดี"
    }
  ];

  const data = [
    { id: 1, name: 'วัตถุประสงค์และขอบเขตโครงงาน', fullscores: '10', score: '' },
    { id: 2, name: 'ความเข้าใจระบบงานเดิม/ทฤษฎีหรืองานวิจัย ที่นำมาใช้พัฒนาโครงงาน', fullscores: '20', score: ''},
    { id: 3, name: 'การศึกษาความต้องการของระบบ และการออกแบบ', fullscores: '20', score: '' },
    { id: 4, name: 'การนำเสนอโครงงาน', fullscores: '20', score: ''},
    { id: 5, name: 'รูปแบบรายงาน', fullscores: '10', score: '' },
    { id: 6, name: 'แนวทางการดำเนินงาน', fullscores: '10', score: '' },
    { id: 7, name: 'คะแนนรวม', fullscores: '90', score: '' }, //ระบบต้องคำนวณคะแนนออกมา
    // Add more data as needed
  ];

  const data2 = [
    { id: 1, name: 'การออกแบบหรือแนวคิด', fullscores: '10', score: 10 },
    { id: 2, name: 'วิธีการ/การดำเนินงาน', fullscores: '20', score: 18 },
    { id: 3, name: 'ความสมบูรณ์ของผลงาน', fullscores: '20', score: 19 },
    { id: 4, name: 'เนื้อหาและรูปแบบของปริญญานิพนธ์', fullscores: '10', score: 9 },
    { id: 5, name: 'การนำเสนอโครงงาน', fullscores: '10', score: 10 },
    { id: 6, name: 'การนำผลงานไปใช้ประโยชน์', fullscores: '5', score: 5 },
    { id: 7, name: 'สรุป/วิจารณ์/การพัฒนาต่อในอนาคต', fullscores: '5', score: 5 },
    { id: 8, name: 'คะแนนรวม', fullscores: '80', score: '78' }, //ระบบต้องคำนวณคะแนนออกมา
    // Add more data as needed
  ];

  const [openPopup, setOpenPopup] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);


  const handleLinkClick = (index) => {
    setOpenPopup(true);
    setSelectedRow(index);
  };

  const handleSavePopup = () => {
    setOpenPopup(false);
    const updatedData = [...tableData];
    updatedData[selectedRow].isRed = false;
    updatedData[selectedRow].isDisabled = true;
    setTableData(updatedData);
  };

  const handleClosePopup = () => {
    setOpenPopup(false);
  };

  const handleRedRow = (index) => {
    const updatedData = [...tableData];
    updatedData[index].isRed = true;
    updatedData[index].isDisabled = true;
    setTableData(updatedData);
  };

  const handleDisableRow = (index) => {
    const updatedData = [...tableData];
    updatedData[index].isDisabled = true;
    setTableData(updatedData);
  };

  const handleDonNotGoAny = () => {
    const updatedData = tableData.map(row => ({ ...row, isRed: true, isDisabled: true }));
    setTableData(updatedData);
  };


  const [ProjectSelect, setProjectSelect] = useState('');
  const [ProjectSelect2, setProjectSelect2] = useState('');
  const [Data, setData] = useState(data);
  const [Data2, setData2] = useState(data2);

  const handleChange = (event) => {
    setProjectSelect(project.find(person => person.ID === event.target.value));
  };

  const handleNameChange = (id, newScore) => {
    setData(prevItems =>
      prevItems.map(item =>
        item.id === id
          ? { ...item, score: newScore } // Update the specific item's name
          : item                       // Keep other items unchanged
      )
    );
  };

  const handleChange2 = (id, field, value) => {
    // Ensure score is not above 100 and is a number
    if (field === 'score' && (isNaN(value) || value > 100)) return;

    setData(prevData =>
      prevData.map(item =>
        item.id === id
          ? { ...item, [field]: value } // Update the specific field
          : item                        // Keep other items unchanged
      )
    );
  };

  return (
    <div>
      <MainCard title="การจัดการโครงงานนักศึกษา">
        <Grid container spacing={gridSpacing}>
          <Grid item xs={12}>
            <Stack spacing={1}>
              <Box display="flex" justifyContent="space-between">
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={handleDonNotGoAny}
                >
                  หยุดทำทุกโครงงาน
                </Button>
              </Box>
            </Stack>
          </Grid>
          <Grid item xs={12}>
            <Box>
              <TableContainer component={Paper}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>ชื่อโครงงาน</TableCell>
                      <TableCell>สถานะ</TableCell>
                      <TableCell>ลิงค์</TableCell>
                      <TableCell>ดำเนินการ</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {tableData.map((row, index) => (
                      <TableRow
                        key={row.id}
                        style={{ backgroundColor: row.isRed ? 'red' : 'white' }}
                      >
                        <TableCell>{row.name}</TableCell>
                        <TableCell>
                          <FormControl fullWidth variant="outlined">
                            <InputLabel id={`status-select-${row.id}`}>สถานะ</InputLabel>
                            <Select
                              labelId={`status-select-${row.id}`}
                              id={`status-select-${row.id}`}
                              value={row.status}
                              onChange={(e) => {
                                const updatedData = [...tableData];
                                updatedData[index].status = e.target.value;
                                setTableData(updatedData);
                              }}
                              label="สถานะ"
                              disabled={row.isDisabled}
                            >
                              <MenuItem value={'CSB01'}>CSB01</MenuItem>
                              <MenuItem value={'CSB02'}>CSB02</MenuItem>
                              <MenuItem value={'CSB03'}>CSB03</MenuItem>
                            </Select>
                          </FormControl>
                        </TableCell>
                        <TableCell>
                          <Button
                            variant="contained"
                            color="primary"
                            onClick={() => handleLinkClick(index)}
                          >
                            เปิด
                          </Button>
                        </TableCell>
                        <TableCell>
                          <Button
                            variant="contained"
                            color="secondary"
                            onClick={() => handleRedRow(index)}
                            disabled={row.isDisabled}
                          >
                            ไม่ให้ทำ
                          </Button>
                          <Button
                            variant="contained"
                            onClick={() => handleDisableRow(index)}
                            disabled={row.isDisabled}
                          >
                            ปิด
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Box>
          </Grid>
        </Grid>
      </MainCard>
      <Dialog open={openPopup} onClose={handleClosePopup}>
        <DialogTitle>ประเมินโครงงาน</DialogTitle>
        <DialogContent>
          <Box mt={2}>
            <FormControl fullWidth variant="outlined">
              <InputLabel id="project-select-label">เลือกโครงงาน</InputLabel>
              <Select
                labelId="project-select-label"
                id="project-select"
                value={ProjectSelect.ID || ''}
                onChange={handleChange}
                label="เลือกโครงงาน"
              >
                {project.map((proj) => (
                  <MenuItem key={proj.ID} value={proj.ID}>
                    {proj.Name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Box>
          <TableContainer component={Paper} style={{ marginTop: '16px' }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>หัวข้อ</TableCell>
                  <TableCell>คะแนนเต็ม</TableCell>
                  <TableCell>คะแนนที่ได้</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {Data.map((row) => (
                  <TableRow key={row.id}>
                    <TableCell>{row.name}</TableCell>
                    <TableCell>{row.fullscores}</TableCell>
                    <TableCell>
                      <TextField
                        type="number"
                        value={row.score}
                        onChange={(e) => handleChange2(row.id, 'score', e.target.value)}
                      />
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClosePopup} color="primary">
            ยกเลิก
          </Button>
          <Button onClick={handleSavePopup} color="primary">
            บันทึก
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default EvaluateProject;
