import React, { useState, useEffect } from 'react';
import { Button, Paper, Grid, Table, TextField, TableBody, TableCell, TableContainer, TableHead, TableRow, Dialog, DialogTitle, DialogContent, DialogActions, Typography } from '@mui/material';
import axios from 'axios';
import { gridSpacing } from 'store/constant';

function InputScoreCSB02() {
  const [projects, setProjects] = useState([]);
  const [open, setOpen] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [Data, setData] = useState([
    { id: 1, name: 'วัตถุประสงค์และขอบเขตโครงงาน', fullscores: '10', score: '' },
    { id: 2, name: 'ความเข้าใจระบบงานเดิม/ทฤษฎีหรืองานวิจัย ที่นำมาใช้พัฒนาโครงงาน', fullscores: '20', score: '' },
    { id: 3, name: 'การศึกษาความต้องการของระบบ และการออกแบบ', fullscores: '20', score: '' },
    { id: 4, name: 'การนำเสนอโครงงาน', fullscores: '20', score: '' },
    { id: 5, name: 'รูปแบบรายงาน', fullscores: '10', score: '' },
    { id: 6, name: 'แนวทางการดำเนินงาน', fullscores: '10', score: '' },
    { id: 7, name: 'คะแนนรวม', fullscores: '90', score: '' }, //ระบบต้องคำนวณคะแนนออกมา
    // Add more data as needed
  ]);

  const handleClosePopup = () => {
    setOpenPopup(false);
  };

  const handleChange4 = (id, field, value) => {
    console.log(value);

    const isValidInput = (input) => {
      // Regular Expression สำหรับการตรวจสอบอักษรพิเศษ
      const regex = /^[0-9]*$/; // อนุญาตเฉพาะตัวเลข 0-9

      return regex.test(input);
    };

    const numericValue = Number(value);

    setData(prevData => {
      // อัปเดตข้อมูลที่ถูกกรอกลงในฟิลด์ score
      const updatedData = prevData.map(item => {
        if (item.id === id) {
          // Convert value to a number
          const numericValue = Number(value);
          // Ensure the value is valid
          if (field === 'score') {
            if (!isValidInput(value) || numericValue < 0 || numericValue > Number(item.fullscores) || isNaN(numericValue)) {
              return item; // If not valid, return the item as is
            }
          }

          // Update the specific field
          return { ...item, [field]: value };
        }

        // Keep other items unchanged
        return item;
      });

      // คำนวณคะแนนรวม
      const totalScore = updatedData.reduce((acc, item) => {
        if (item.id !== 7) {
          return acc + Number(item.score || 0); // รวมคะแนนของรายการที่ไม่ใช่ id 7
        }
        return acc;
      }, 0);

      // อัปเดตคะแนนรวมใน id: 7
      return updatedData.map(item => {
        if (item.id === 7) {
          return { ...item, score: totalScore.toString() };
        }
        return item;
      });
    });
  };


  // State for subject names fetched from the database
  const [examscores, setExamscores] = useState([]);

  useEffect(() => {
    const fetchExamscores = async () => {
      try {
        const response = await axios.get('http://localhost:9999/Exam_results');
        if (response.data && Array.isArray(response.data)) {
          setExamscores(response.data.map(item => item.Er_name));
        }
      } catch (error) {
        console.error('Error fetching subject names:', error);
      }
    };

    fetchExamscores();
  }, []);


  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:9999/Exam_results', formData);
      console.log('Subject added:', response.data);
      alert("Subject added successfully!");
      // Reset form after successful submission
      setFormData({
        Er_name: '',
        Er_results: '',
        Er_com: '',
        Er_status: '',
        Er_date: '',
        Er_time: ''
      });
    } catch (error) {
      console.error('Error adding subject:', error);
      alert("Failed to add subject. Please try again later.");
    }
  };

  // Handle change function to update the selected project
  const handleChange = (event) => {
    const selectedProjectName = event.target.value;
    const selectedProject = project.find((proj) => proj.Name === selectedProjectName);

    // Update the state with the selected project's details
    if (selectedProject) {
      setProjectSelect(selectedProject);
    } else {
      setProjectSelect({
        ID: '',
        Name: '',
        SID: '',
        SName: '',
        SID2: '',
        SName2: '',
        TID: '',
        TName: '',
      });
    }
  };

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get('http://localhost:9999/Project');
        setProjects(response.data); // Assuming response.data contains the list of projects
      } catch (error) {
        console.error('Error fetching projects:', error);
      }
    };

    fetchProjects();
  }, []);

  useEffect(() => {
    updateFinalScore();
    checkAllChecked();
  }, [Data]);

  const handleDonNotGoAny = () => {
    const updatedData = projects.map(project => ({ ...project, isRed: true, isDisabled: true }));
    setProjects(updatedData);
  };

  const handleLinkClick = (index) => {
    setSelectedProject(projects[index]);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    resetCheckboxData(); // Reset checkbox values when closing the dialog
  };

  const handleDisableRow = (index) => {
    const updatedProjects = projects.map((project, i) =>
      i === index ? { ...project, isDisabled: true } : project
    );
    setProjects(updatedProjects);
  };

  const handleRedRow = (index) => {
    const updatedProjects = projects.map((project, i) =>
      i === index ? { ...project, isRed: true } : project
    );
    setProjects(updatedProjects);
  };

  const handleCheckboxChange = (id, type) => {
    setData(prevData =>
      prevData.map(item =>
        item.id === id
          ? { ...item, score: type === 'yes' ? 'ผ่าน' : 'ไม่ผ่าน' }
          : item
      )
    );
  };

  const updateFinalScore = () => {
    const countPass = Data
      .filter(item => item.id >= 1 && item.id <= 7)
      .filter(item => item.score === 'ผ่าน').length;

    const countFail = Data
      .filter(item => item.id >= 1 && item.id <= 7)
      .filter(item => item.score === 'ไม่ผ่าน').length;

    if (countFail >= 4) {
      setData(prevData =>
        prevData.map(item =>
          item.id === 8
            ? { ...item, score: 'ไม่ผ่านการประเมินหัวข้อ' }
            : item
        )
      );
    } else if (countPass >= 4) {
      setData(prevData =>
        prevData.map(item =>
          item.id === 8
            ? { ...item, score: 'ผ่านการประเมินหัวข้อ' }
            : item
        )
      );
    }
  };

  const checkAllChecked = () => {
    const allChecked = Data
      .filter(item => item.id >= 1 && item.id <= 7)
      .every(item => item.score === 'ผ่าน' || item.score === 'ไม่ผ่าน');
    setAllChecked(allChecked);
  };

  const handleSavePopup = async (e) => {
    if (allChecked) {
      e.preventDefault();
      await saveScore();

      // Remove the evaluated project from the list
      setProjects(prevProjects => prevProjects.filter(project => project.P_id !== selectedProject.P_id));

      setOpen(false); // Close the popup after saving
    } else {
      alert('กรุณาตรวจสอบให้ครบทุกหัวข้อก่อนทำการบันทึก');
    }
  };

  const resetCheckboxData = () => {
    setData(initialData); // Reset to initial state
    setAllChecked(false); // Reset allChecked state
  };

  const saveScore = async () => {
    const lastScore = Data[Data.length - 1].score;
    const erStatus = lastScore === 'ผ่านการประเมินหัวข้อ' ? 'ผ่าน' : 'ไม่ผ่าน'; // Determine Er_status based on Er_results
    const erName = selectedProject.P_name; // Get the project name for Er_name

    try {
      const response = await axios.post('http://localhost:9999/Exam_results', {
        Er_name: erName, // Use the project's name
        Er_CSB01: lastScore
      });

      if (response.status === 200) {
        console.log('Score saved successfully!');
        setOpenDialog(true); // Open success dialog on successful save
      } else {
        console.error('Failed to save score');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
<Paper style={{ padding: 16 }}>
      <Grid container spacing={gridSpacing}>
        <Grid item xs={12}>
          <Grid container direction="column" spacing={1}>
            <h1>ประเมินการสอบโครงงานพิเศษ</h1>
            <Button onClick={handleDonNotGoAny} variant="contained" color="error">
              ไม่เข้าร่วมประเมินทั้งหมด
            </Button>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ลำดับที่</TableCell>
                    <TableCell>ชื่อโครงงาน</TableCell>
                    <TableCell>ประเมินการสอบโครงงานพิเศษ</TableCell>
                    <TableCell>ไม่ประสงค์ลงคะแนนสอบ</TableCell>
                    <TableCell>ไม่เข้าประเมินการสอบ</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {projects.map((project, index) => (
                    <TableRow key={project.P_id}>
                      <TableCell>{index + 1}</TableCell>
                      <TableCell>{project.P_name}</TableCell>
                      <TableCell>
                        <Button
                          onClick={() => handleLinkClick(index)}
                          variant="outlined"
                          disabled={project.isDisabled}
                        >
                          ประเมิน
                        </Button>
                      </TableCell>
                      <TableCell>
                        <Button
                          onClick={() => handleDisableRow(index)}
                          variant="outlined"
                          disabled={project.isDisabled || project.isRed}
                        >
                          ไม่ประสงค์ลงคะแนน
                        </Button>
                      </TableCell>
                      <TableCell>
                        <Button
                          onClick={() => handleRedRow(index)}
                          variant="outlined"
                          disabled={project.isDisabled || project.isRed}
                        >
                          ไม่เข้าประเมิน
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Grid>
        </Grid>
      </Grid>

      {selectedProject && (
        <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
          <DialogTitle>รายละเอียดโครงงาน</DialogTitle>
          <DialogContent>
            <Typography variant="h6">ชื่อโครงงาน: {selectedProject.P_name}</Typography>
            <Typography>นักเรียนคนที่ 1: {selectedProject.P_S1}</Typography>
            <Typography>นักเรียนคนที่ 2: {selectedProject.P_S2}</Typography>
            <Typography>ที่ปรึกษา: {selectedProject.P_T}</Typography>
            <h2>ตารางลงคะแนนสำหรับกรรมการสอบ</h2>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ลำดับที่</TableCell>
                    <TableCell>เกณฑ์พิจารณา</TableCell>
                    <TableCell>คำอธิบาย</TableCell>
                    <TableCell>ผลการประเมิน</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {Data.map((item, index) => (
                    <TableRow key={item.id}>
                      <TableCell>{item.id}</TableCell>
                      <TableCell>{item.name}</TableCell>
                      <TableCell>
                        <ul>
                          {(item.description || []).map((desc, idx) => (
                            <li key={idx}>{desc}</li>
                          ))}
                        </ul>
                      </TableCell>
                      <TableCell>
                        {item.id === 8 ? (
                          <TextField value={item.score} disabled fullWidth margin="none" />
                        ) : (
                          <div>
                            <label htmlFor={`pass-${index}`}>
                              <input
                                type="radio"
                                id={`pass-${index}`}
                                checked={item.score === "ผ่าน"}
                                onChange={() => handleCheckboxChange(item.id, "yes")}
                              />
                              ผ่าน
                            </label>
                            <label htmlFor={`fail-${index}`}>
                              <input
                                type="radio"
                                id={`fail-${index}`}
                                checked={item.score === "ไม่ผ่าน"}
                                onChange={() => handleCheckboxChange(item.id, "no")}
                              />
                              ไม่ผ่าน
                            </label>
                          </div>
                        )}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </DialogContent>

          <DialogActions>
            <Button onClick={handleSavePopup} color="primary" disabled={!allChecked}>บันทึก</Button>
            <Button onClick={handleClose} color="primary">ปิด</Button>
          </DialogActions>
        </Dialog>
      )}

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>ทำการบันทึกสำเร็จ !!</DialogTitle>
        <DialogContent>
          <p>รอการตรวจสอบจากเจ้าหน้าที่</p>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Close</Button>
        </DialogActions>
      </Dialog>
    </Paper>
  );
}

export default InputScoreCSB02;
