import React, { useState, useEffect } from 'react';
import { Button, TableCell, TableContainer, Table, TableHead, TableBody, TableRow, Dialog, DialogContent, DialogActions, DialogTitle } from '@mui/material';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';

function RequestAdvisor() {
    const [tableData, setTableData] = useState([]);
    const [openPopup, setOpenPopup] = useState(false);
    const [openAcceptDialog, setOpenAcceptDialog] = useState(false);
    const [openConfirmationDialog, setOpenConfirmationDialog] = useState(false); // New state for confirmation dialog
    const [selectedProject, setSelectedProject] = useState(null);

    useEffect(() => {
        // Fetch data from API
        fetch('http://localhost:9999/Project')
            .then(response => response.json())
            .then(data => {
                setTableData(data);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }, []);

    const handlePopupClick = (project) => {
        setSelectedProject(project);
        setOpenPopup(true);
    };

    const handlePopupClose = () => {
        setOpenPopup(false);
        setSelectedProject(null);
    };

    const handleOpenAcceptDialog = (project) => {
        setSelectedProject(project);
        setOpenAcceptDialog(true);
    };

    const handleCloseAcceptDialog = () => {
        setOpenAcceptDialog(false);
        setSelectedProject(null);
    };

    const handleConfirmAccept = () => {
        if (selectedProject) {
            console.log(`Accept advisor for project ID: ${selectedProject.ID}`);

            // Update the project status on the server
            fetch(`http://localhost:9999/Project/${selectedProject.ID}`, { // Include the specific project ID in the URL
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ P_status: 'กำลังดำเนินการ' }), // Update status to "In Progress"
            })
            .then(response => response.json())
            .then(() => {
                // Remove only the confirmed project from the table
                setTableData(prevData => prevData.filter(project => project.ID !== selectedProject.ID));
                handleCloseAcceptDialog();
                setOpenConfirmationDialog(true); // Show the new confirmation dialog
            })
            .catch(error => {
                console.error('Error updating project status:', error);
            });
        }
    };


    const handleCloseConfirmationDialog = () => {
        setOpenConfirmationDialog(false);
    };

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Grid container direction="column" spacing={1}></Grid>
                            <div>
                                <h1>สถานะคำร้องขอเป็นอาจารย์ที่ปรึกษา</h1>
                                <TableContainer>
                                    <Table>
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>ลำดับที่</TableCell>
                                                <TableCell>ชื่อโครงงาน</TableCell>
                                                <TableCell>นักศึกษาคนที่ 1</TableCell>
                                                <TableCell>นักศึกษาคนที่ 2</TableCell>
                                                <TableCell>รายละเอียดโครงงาน</TableCell>
                                                <TableCell>จัดการ</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {tableData.length > 0 ? (
                                                tableData.map((row, index) => (
                                                    <TableRow key={index}>
                                                        <TableCell>{index + 1}</TableCell>
                                                        <TableCell>{row.P_name}</TableCell>
                                                        <TableCell>{row.P_S1}</TableCell>
                                                        <TableCell>{row.P_S2}</TableCell>
                                                        <TableCell>
                                                            <Button onClick={() => handlePopupClick(row)}>ดูรายละเอียด</Button>
                                                        </TableCell>
                                                        <TableCell>
                                                            <Button onClick={() => handleOpenAcceptDialog(row)}>รับเป็นที่ปรึกษา</Button>
                                                        </TableCell>
                                                    </TableRow>
                                                ))
                                            ) : (
                                                <TableRow>
                                                    <TableCell colSpan={6} align="center">
                                                        No data available
                                                    </TableCell>
                                                </TableRow>
                                            )}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                                <Dialog open={openPopup} onClose={handlePopupClose}>
                                    <DialogContent>
                                        {selectedProject ? (
                                            <div>
                                                <h2>{selectedProject.P_name}</h2>
                                                <p>{selectedProject.P_details}</p>
                                                <p>{selectedProject.P_type}</p>
                                                <p>{selectedProject.P_tool}</p>
                                            </div>
                                        ) : (
                                            'No project details available'
                                        )}
                                    </DialogContent>
                                    <DialogActions>
                                        <Button onClick={handlePopupClose}>Close</Button>
                                    </DialogActions>
                                </Dialog>
                                <Dialog open={openAcceptDialog} onClose={handleCloseAcceptDialog}>
                                    <DialogTitle>ยืนยันการรับเป็นที่ปรึกษา</DialogTitle>
                                    <DialogContent>
                                        คุณยินยอมเป็นที่ปรึกษาโครงการนี้ใช่หรือไม่?
                                    </DialogContent>
                                    <DialogActions>
                                        <Button onClick={handleCloseAcceptDialog}>ยกเลิก</Button>
                                        <Button onClick={handleConfirmAccept}>ยืนยัน</Button>
                                    </DialogActions>
                                </Dialog>  
                                <Dialog open={openConfirmationDialog} onClose={handleCloseConfirmationDialog}>
                                    <DialogTitle>การยืนยัน</DialogTitle>
                                    <DialogContent>
                                        คุณได้เป็นที่ปรึกษาโครงงานนี้เรียบร้อยแล้ว
                                    </DialogContent>
                                    <DialogActions>
                                        <Button onClick={handleCloseConfirmationDialog}>ปิด</Button>
                                    </DialogActions>
                                </Dialog>
                            </div>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
}

export default RequestAdvisor;
