import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import { Stack } from '@mui/system';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';

function ChairmanScoreCSB02() {
    const [project, setProject] = useState([]);
    const [projectSelect, setProjectSelect] = useState(null);
    const [erCSB02, setErCSB02] = useState(null);
    const [openDialog, setOpenDialog] = useState(false);
    const [editedScore, setEditedScore] = useState('');

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const response = await axios.get('http://localhost:9999/Project');
                if (response.data && Array.isArray(response.data)) {
                    setProject(response.data);
                }
            } catch (error) {
                console.error('Error fetching project data:', error);
            }
        };

        const fetchErCSB02 = async () => {
            try {
                const response = await axios.get('http://localhost:9999/Exam_results');
                if (response.data) {
                    const result = response.data.find(item => item.Er_Pname === projectSelect?.P_name);
                    setErCSB02(result);
                }
            } catch (error) {
                console.error('Error fetching Er_CSB02 data:', error);
            }
        };

        fetchProjects();
        if (projectSelect) {
            fetchErCSB02();
        }
    }, [projectSelect]);

    const handleChange = (event) => {
        setProjectSelect(project.find(p => p.P_name === event.target.value));
    };

    const handleEditClick = () => {
        if (erCSB02) {
            setEditedScore(erCSB02.Er_CSB02 || '');
        }
        setOpenDialog(true);
    };

    const handleDialogClose = () => {
        setOpenDialog(false);
    };

    const handleScoreChange = (event) => {
        setEditedScore(event.target.value);
    };

    const handleSave = () => {
        // Logic to save the edited score goes here
        console.log('Saving score:', editedScore);
        setOpenDialog(false);
    };

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Grid container direction="column" spacing={1}>
                                <Box fontSize='18px' sx={{ marginTop: 5 }}>
                                    <h1>แบบประเมินโครงงานพิเศษ 1 (สอบก้าวหน้า)</h1>
                                    <p>เลือกชื่อโครงงาน</p>
                                    <FormControl>
                                        <InputLabel id="ProjectName-select-label">ชื่อโครงงาน</InputLabel>
                                        <Select
                                            labelId="ProjectName-select-label"
                                            value={projectSelect?.P_name || ''}
                                            onChange={handleChange}
                                            margin="normal"
                                            sx={{ minWidth: 150 }}
                                        >
                                            {project.map((p) => (
                                                <MenuItem
                                                    key={p.P_id}
                                                    value={p.P_name}
                                                >
                                                    {p.P_name}
                                                </MenuItem>
                                            ))}
                                        </Select>
                                    </FormControl>
                                    <br />
                                    <p>โดย</p>
                                    <TextField
                                        label="ชื่อ-สกุลนักศึกษาคนที่ 1"
                                        value={projectSelect?.P_S1 || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                    />
                                    <TextField
                                        label="ชื่อ-สกุลนักศึกษาคนที่ 2"
                                        value={projectSelect?.P_S2 || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                    />
                                    <p>อาจารย์ที่ปรึกษา</p>
                                    <TextField
                                        label="ชื่ออาจารย์ที่ปรึกษา"
                                        value={projectSelect?.P_T || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '40ch' } }}
                                    />
                                    <br />
                                    {projectSelect && erCSB02 && (
                                        <>
                                            <h2>ตารางคะแนน</h2>
                                            <TableContainer component={Paper}>
                                                <Table>
                                                    <TableHead>
                                                        <TableRow>
                                                            <TableCell>คะแนนเต็ม</TableCell>
                                                            <TableCell>คะแนนได้</TableCell>
                                                        </TableRow>
                                                    </TableHead>
                                                    <TableBody>
                                                        <TableRow>
                                                            <TableCell>90</TableCell>
                                                            <TableCell>{erCSB02?.Er_CSB02 || 'N/A'}</TableCell>
                                                        </TableRow>
                                                    </TableBody>
                                                </Table>
                                            </TableContainer>
                                            <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
                                                <Button variant="contained" color="primary" onClick={handleEditClick}>
                                                    แก้ไข
                                                </Button>
                                            </Stack>
                                        </>
                                    )}
                                </Box>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
            <Dialog open={openDialog} onClose={handleDialogClose}>
                <DialogTitle>แก้ไขคะแนน</DialogTitle>
                <DialogContent>
                    <TextField
                        label="คะแนนที่ได้"
                        value={editedScore}
                        onChange={handleScoreChange}
                        fullWidth
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleDialogClose} color="primary">
                        ยกเลิก
                    </Button>
                    <Button onClick={handleSave} color="primary">
                        บันทึก
                    </Button>
                </DialogActions>
            </Dialog>
        </MainCard>
    );
}

export default ChairmanScoreCSB02;
