    { id: 2, name: 'ความเข้าใจระบบงานเดิม/ทฤษฎีหรืองานวิจัย ที่นำมาใช้พัฒนาโครงงาน', fullscores: '20', score: '' },
    { id: 3, name: 'การศึกษาความต้องการของระบบ และการออกแบบ', fullscores: '20', score: '' },
    { id: 4, name: 'การนำเสนอโครงงาน', fullscores: '20', score: '' },
    { id: 5, name: 'รูปแบบรายงาน', fullscores: '10', score: '' },
    { id: 6, name: 'แนวทางการดำเนินงาน', fullscores: '10', score: '' },
    { id: 7, name: 'คะแนนรวม', fullscores: '90', score: '' }
  ];

  const data2 = [
    { id: 1, name: 'การออกแบบหรือแนวคิด', fullscores: '10', score: 10 },
    { id: 2, name: 'วิธีการ/การดำเนินงาน', fullscores: '20', score: 18 },
    { id: 3, name: 'ความสมบูรณ์ของผลงาน', fullscores: '20', score: 19 },
    { id: 4, name: 'เนื้อหาและรูปแบบของปริญญานิพนธ์', fullscores: '10', score: 9 },
    { id: 5, name: 'การนำเสนอโครงงาน', fullscores: '10', score: 10 },
    { id: 6, name: 'การนำผลงานไปใช้ประโยชน์', fullscores: '5', score: 5 },
    { id: 7, name: 'สรุป/วิจารณ์/การพัฒนาต่อในอนาคต', fullscores: '5', score: 5 },
    { id: 8, name: 'คะแนนรวม', fullscores: '80', score: '78' }
  ];

  const [openPopup, setOpenPopup] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);
  const [ProjectSelect, setProjectSelect] = useState('');
  const [ProjectSelect2, setProjectSelect2] = useState('');
  const [Data, setData] = useState(data);
  const [Data2, setData2] = useState(data2);
  const [openDialog, setOpenDialog] = useState(false);
  const [openDialog2, setOpenDialog2] = useState(false);

  const handleLinkClick = (index) => {
    setOpenPopup(true);
    setSelectedRow(index);
  };

  const handleSavePopup = () => {
    setOpenPopup(false);
    const updatedData = [...tableData];
    updatedData[selectedRow].isRed = false;
    updatedData[selectedRow].isDisabled = true;
    setTableData(updatedData);
  };

  const handleClosePopup = () => {
    setOpenPopup(false);
  };

  const handleRedRow = (index) => {
    const updatedData = [...tableData];
    updatedData[index].isRed = true;
    updatedData[index].isDisabled = true;
    setTableData(updatedData);
  };

  const handleDisableRow = (index) => {
    const updatedData = [...tableData];
    updatedData[index].isDisabled = true;
    setTableData(updatedData);
  };

  const handleDonNotGoAny = () => {
    const updatedData = tableData.map(row => ({ ...row, isRed: true, isDisabled: true }));
    setTableData(updatedData);
  };

  const handleChange = (event) => {
    setProjectSelect(project.find(person => person.ID === event.target.value));
  };

  const handleNameChange = (id, newScore) => {
    setData(prevItems =>
      prevItems.map(item =>
        item.id === id
          ? { ...item, score: newScore }
          : item
      )
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleSubmit2 = (e) => {
    e.preventDefault();
    setOpenDialog2(true);
  };

  const handleCloseDialog2 = () => {
    setOpenDialog2(false);
  };

  const handleChange2 = (id, value) => {
    if (isNaN(value) || value > 100) return;

    setData(prevData =>
      prevData.map(item =>
        item.id === id
          ? { ...item, score: value }
          : item
      )
    );
  };

  return (
    <MainCard>
      <Grid container spacing={gridSpacing}>
        <Grid item xs={12}>
          <Grid container alignItems="center" justifyContent="space-between">
            <Grid item>
              <Grid container direction="column" spacing={1}>
                <Grid item>
                  <Stack>
                    <Select
                      value={ProjectSelect ? ProjectSelect.ID : ''}
                      onChange={handleChange}
                      displayEmpty
                      inputProps={{ 'aria-label': 'Select project' }}
                    >
                      <MenuItem value="" disabled>
                        โปรดเลือกโครงงาน
                      </MenuItem>
                      {project.map((proj) => (
                        <MenuItem key={proj.ID} value={proj.ID}>
                          {proj.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </Stack>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </Grid>

        <Grid item xs={12}>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>โครงงาน</TableCell>
                  <TableCell>ไฟล์ที่อัพโหลด</TableCell>
                  <TableCell>สถานะ</TableCell>
                  <TableCell>Action</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {tableData.map((row, index) => (
                  <TableRow key={row.id} className={row.isRed ? 'red-row' : ''}>
                    <TableCell>{row.name}</TableCell>
                    <TableCell>
                      <Button onClick={() => handleLinkClick(index)}>{row.link}</Button>
                    </TableCell>
                    <TableCell>{row.status}</TableCell>
                    <TableCell>
                      <Button disabled={row.isDisabled} onClick={() => handleRedRow(index)}>Disable</Button>
                      <Button disabled={row.isDisabled} onClick={() => handleDisableRow(index)}>Red</Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Grid>
      </Grid>

      <Dialog open={openPopup} onClose={handleClosePopup}>
        <DialogTitle>Form</DialogTitle>
        <DialogContent>
          <Box component="form" onSubmit={handleSavePopup}>
            {Data.map((item) => (
              <TextField
                key={item.id}
                label={item.name}
                value={item.score}
                onChange={(e) => handleChange2(item.id, e.target.value)}
                fullWidth
                margin="normal"
              />
            ))}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClosePopup}>Cancel</Button>
          <Button type="submit">Save</Button>
        </DialogActions>
      </Dialog>

      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>Form Submission</DialogTitle>
        <DialogContent>
          <Box component="form" onSubmit={handleSubmit}>
            {Data2.map((item) => (
              <TextField
                key={item.id}
                label={item.name}
                value={item.score}
                fullWidth
                margin="normal"
                InputProps={{
                  readOnly: true
                }}
              />
            ))}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Close</Button>
        </DialogActions>
      </Dialog>
    </MainCard>
  );
}

export default EvaluateProject;
