import React, { useState } from 'react';
import { TextField, FormControl, InputLabel, Grid, TypographyมMenuItem } from '@mui/material';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';

const ManageExamRoom = () => {
    const [option, setOption] = useState('');
    const [room, setRoom] = useState('');
    const [date, setDate] = useState(null);

    // Mock data for select options
    const testData = [
        { ID: 'CSB01', Name: 'สอบหัวข้อ' },
        { ID: 'CSB02', Name: 'สอบก้าวหน้า' },
        { ID: 'CSB03', Name: 'สอบป้องกัน' }
    ];

    const roomData = [
        { ID: '78-618' },
        { ID: '78-618/2' },
        { ID: '78-619' },
        { ID: '78-621' },
        { ID: '78-617' }
    ];

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Typography variant="h2" align="center" gutterBottom>จัดห้องสอบ</Typography>
                </Grid>
                <Grid item xs={12}>
                    <Grid container spacing={2}>
                        {/* Select for test type */}
                        <Grid item xs={12} md={4}>
                            <FormControl fullWidth>
                                <InputLabel id="option-label">เลือกการสอบ</InputLabel>
                                <Select
                                    labelId="option-label"
                                    value={option}
                                    onChange={(e) => setOption(e.target.value)}
                                >
                                    {testData.map((test) => (
                                        <MenuItem key={test.ID} value={test.ID}>
                                            {test.ID + ' : ' + test.Name}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Grid>
                        {/* Select for exam room */}
                        <Grid item xs={12} md={4}>
                            <FormControl fullWidth>
                                <InputLabel id="room-label">ห้องสอบ</InputLabel>
                                <Select
                                    labelId="room-label"
                                    value={room}
                                    onChange={(e) => setRoom(e.target.value)}
                                >
                                    {roomData.map((room) => (
                                        <MenuItem key={room.ID} value={room.ID}>
                                            {room.ID}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Grid>
                        {/* Date picker */}
                        <Grid item xs={12} md={4}>
                            <FormControl fullWidth>
                                <LocalizationProvider dateAdapter={AdapterDayjs}>
                                    <DatePicker
                                        label="วันที่สอบ"
                                        value={date}
                                        onChange={(newValue) => setDate(newValue)}
                                        renderInput={(params) => <TextField {...params} fullWidth />}
                                    />
                                </LocalizationProvider>
                            </FormControl>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
};

export default ManageExamRoom;
