        const newTime = event.target.value;
        const newTimeOn1 = [...timeOn1];
        newTimeOn1[index] = newTime;

        // Check for duplicates
        const timeCounts = newTimeOn1.reduce((acc, time) => {
            acc[time] = (acc[time] || 0) + 1;
            return acc;
        }, {});
        const newInvalidTimes = newTimeOn1.map((time, i) => timeCounts[time] > 1 && i !== newTimeOn1.indexOf(time));

        setTimeOn1(newTimeOn1);
        setInvalidTimes(newInvalidTimes);
    };

    const getFilteredTeachers = (index) => {
        const selectedTeachers = teacherValues.filter((_, i) => i !== index);
        return Teacher.filter((teacher) => !selectedTeachers.includes(teacher.ID));
    };

    const getFilteredPositions = (index) => {
        const selectedPositions = positionValues.filter((_, i) => i !== index);
        return positionData.filter((position) => !selectedPositions.includes(position.ID) || position.ID !== 'No1');
    };

    const getFilteredProject = (index) => {
        const selectedProject = ProjectValues.filter((_, i) => i !== index);
        return ProjectData.filter((ProjectData) => !selectedProject.includes(ProjectData.ID));
    };

    const handleNumOfMembersChange = (event) => {
        const newNumOfMembers = parseInt(event.target.value) || 0;
        setNumOfMembers(newNumOfMembers);
        setMembers(Array(newNumOfMembers).fill({ teacher: '', position: '' }));
    };

    const handleMemberChange = (index, field) => (event) => {
        const newMembers = [...members];
        newMembers[index][field] = event.target.value;

        // If the position is changed to 'Chair', set that member as chairman
        if (field === 'position') {
            if (event.target.value === 'Chair') {
                setChairman(newMembers[index].teacher);
            } else if (chairman === newMembers[index].teacher) {
                setChairman('');
            }
        }

        setMembers(newMembers);
    };

    const getAvailableTeachers = () => {
        return teacherData.filter(teacher => !members.some(member => member.teacher === teacher.ID));
    };

    const getAvailablePositions = () => {
        return [
            { value: 'Chair', label: 'ประธาน' },
            { value: 'Member', label: 'กรรมการสอบ' }
        ];
    };

    const validateMembers = () => {
        const teachers = new Set();
        let isValid = true;
        members.forEach(member => {
            if (teachers.has(member.teacher)) {
                isValid = false;
            } else {
                teachers.add(member.teacher);
            }
        });
        return isValid && chairman !== '';
    };

    const handleSave = () => {
        if (validateMembers()) {
            console.log('Save clicked');
        } else {
            alert('กรุณาตรวจสอบการเลือกกรรมการและประธาน');
        }
    };

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Typography variant="h2" align="center" gutterBottom>จัดห้องสอบ</Typography>
                </Grid>
                <Grid item xs={12}>
                    <Grid container spacing={2}>
                        {/* Select for test type */}
                        <Grid item xs={12} md={4}>
                            <FormControl fullWidth>
                                <InputLabel id="option-label">เลือกการสอบ</InputLabel>
                                <Select
                                    labelId="option-label"
                                    value={option}
                                    onChange={(e) => setOption(e.target.value)}
                                >
                                    {testData.map((test) => (
                                        <MenuItem key={test.ID} value={test.ID}>
                                            {test.ID + ' : ' + test.Name}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Grid>
                        {/* Select for exam room */}
                        <Grid item xs={12} md={4}>
                            <FormControl fullWidth>
                                <InputLabel id="room-label">ห้องสอบ</InputLabel>
                                <Select
                                    labelId="room-label"
                                    value={room}
                                    onChange={(e) => setRoom(e.target.value)}
                                >
                                    {roomData.map((room) => (
                                        <MenuItem key={room.ID} value={room.ID}>
                                            {room.ID}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </Grid>
                        {/* Date picker */}
                        <Grid item xs={12} md={4}>
                            <FormControl fullWidth>
                                <LocalizationProvider dateAdapter={AdapterDayjs}>
                                    <DatePicker
                                        label="วันที่สอบ"
                                        value={date}
                                        onChange={(newValue) => setDate(newValue)}
                                        renderInput={(params) => <TextField {...params} fullWidth />}
                                    />
                                </LocalizationProvider>
                            </FormControl>
                        </Grid>
                        {/* Number of examiners */}
                        <Grid item xs={12}>
                            <Typography variant="h6" gutterBottom>
                                จำนวนกรรมการสอบ
                            </Typography>
                            <TextField
                                label="จำนวนกรรมการสอบ"
                                type="number"
                                value={numOfMembers}
                                onChange={handleNumOfMembersChange}
                                fullWidth
                            />
                        </Grid>
                        {/* Examiners info */}
                        {members.map((member, index) => (
                            <Grid item xs={12} key={index}>
                                <Typography sx={{ marginTop: 3 }} fontSize={'20px'}>
                                    กรรมการสอบ {index + 1}
                                </Typography>
                                <Grid container spacing={2}>
                                    <Grid item xs={12} md={6}>
                                        <FormControl fullWidth>
                                            <InputLabel id={`teacher-label-${index}`}>ชื่อกรรมการ</InputLabel>
                                            <Select
                                                labelId={`teacher-label-${index}`}
                                                value={member.teacher}
                                                onChange={handleMemberChange(index, 'teacher')}
                                                disabled={member.position === 'Chair'}
                                            >
                                                {getAvailableTeachers().map((teacher) => (
                                                    <MenuItem key={teacher.ID} value={teacher.ID}>
                                                        {teacher.Name}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    </Grid>
                                    <Grid item xs={12} md={6}>
                                        <FormControl fullWidth>
                                            <InputLabel id={`position-label-${index}`}>ตำแหน่ง</InputLabel>
                                            <Select
                                                labelId={`position-label-${index}`}
                                                value={member.position}
                                                onChange={handleMemberChange(index, 'position')}
                                            >
                                                {getAvailablePositions().map((position) => (
                                                    <MenuItem key={position.value} value={position.value}>
                                                        {position.label}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    </Grid>
                                </Grid>
                            </Grid>
                        ))}
                        <Grid item xs={12}>
                            <Button variant="contained" color="primary" onClick={handleSave}>
                                บันทึก
                            </Button>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
};

export default ManageExamRoom;
