import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    FormControl,
    Grid,
    InputLabel,
    MenuItem,
    Select,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    TextField,
    Paper
} from '@mui/material';
import MainCard from './MainCard'; // Import your MainCard component

function ChairmanScoreCSB03() {
    const [projectSelect, setProjectSelect] = useState(null);
    const [editingRowId, setEditingRowId] = useState(null);
    const [openDialog, setOpenDialog] = useState(false);
    const [data, setData] = useState([
        { id: 1, name: 'การออกแบบหรือแนวคิด', fullscores: '10', score: '' },
        { id: 2, name: 'วิธีการ/การดำเนินงาน', fullscores: '20', score: '' },
        { id: 3, name: 'ความสมบูรณ์ของผลงาน', fullscores: '20', score: '' },
        { id: 4, name: 'เนื้อหาและรูปแบบของปริญญานิพนธ์', fullscores: '10', score: '' },
        { id: 5, name: 'การนำเสนอโครงงาน', fullscores: '10', score: '' },
        { id: 6, name: 'การนำผลงานไปใช้ประโยชน์', fullscores: '5', score: '' },
        { id: 7, name: 'สรุป/วิจารณ์/การพัฒนาต่อในอนาคต', fullscores: '5', score: '' },
        { name: 'คะแนนรวม', fullscores: '80', score: '' },
    ]);
    const [projects, setProjects] = useState([]); // State for storing project data

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const response = await axios.get('http://localhost:9999/Project');
                // Assuming the response data contains Er_Pname
                const filteredProjects = response.data.filter(project =>
                    project.Er_Pname && project.Er_CSB03 && project.Er_CSB03_status
                );
                setProjects(filteredProjects || []);
            } catch (error) {
                console.error('Error fetching projects:', error);
            }
        };

        fetchProjects();
    }, []);

    const handleNameChange = (id, value) => {
        const item = data.find(item => item.id === id);

        if (/^\d*$/.test(value) && Number(value) <= Number(item.fullscores)) {
            setData(prevData =>
                prevData.map(item =>
                    item.id === id ? { ...item, score: value } : item
                )
            );
        }
    };

    const calculateTotalScore = () => {
        const totalScore = data
            .filter(item => item.id >= 1 && item.id <= 7)
            .reduce((acc, curr) => acc + Number(curr.score), 0);

        setData(prevData =>
            prevData.map(item =>
                item.name === 'คะแนนรวม' ? { ...item, score: totalScore } : item
            )
        );
    };

    const handleChange = (event) => {
        const selectedProject = projects.find(project => project.Er_Pname === event.target.value);
        setProjectSelect(selectedProject);
        // Resetting data to ensure table updates
    };

    const handleEditClick = (id) => {
        setEditingRowId(id);
    };

    const handleSaveClick = () => {
        setEditingRowId(null);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
    };

    useEffect(() => {
        calculateTotalScore();
    }, [data]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:9999/Exam_results', data);
            console.log('Subject added:', response.data);
            alert("Subject added successfully!");
            setOpenDialog(true); // Open dialog on successful submission
        } catch (error) {
            console.error('Error adding subject:', error);
            alert("Failed to add subject. Please try again later.");
        }
    };

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Box fontSize='18px' sx={{ marginTop: 5 }}>
                                <h1>แบบประเมินโครงงานพิเศษ 2 (ปริญญานิพนธ์)</h1>
                                <p>
                                    เลือกชื่อโครงงาน
                                </p>
                                <FormControl>
                                    <InputLabel id="project-name-select-label">ชื่อโครงงาน</InputLabel>
                                    <Select
                                        labelId="project-name-select-label"
                                        value={projectSelect?.Er_Pname || ''}
                                        onChange={handleChange}
                                        label="ชื่อโครงงาน"
                                        margin="normal"
                                        sx={{ minWidth: 150 }}
                                    >
                                        {projects.map((proj) => (
                                            <MenuItem
                                                key={proj.ID}
                                                value={proj.Er_Pname} // Use Er_Pname as the value
                                            >
                                                {proj.Er_Pname} // Display Er_Pname
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                                <br />
                                <p>โดย</p>
                                <TextField
                                    label="รหัสนักศึกษาคนที่ 1"
                                    value={projectSelect?.SID || ''}
                                    disabled
                                    sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                />
                                <TextField
                                    label="ชื่อ-สกุลนักศึกษาคนที่ 1"
                                    value={projectSelect?.SName || ''}
                                    disabled
                                    sx={{ '& > :not(style)': { mr: 0, width: '30ch' } }}
                                /><br />
                                <TextField
                                    label="รหัสนักศึกษาคนที่ 2"
                                    value={projectSelect?.SID2 || ''}
                                    disabled
                                    sx={{ '& > :not(style)': { mr: 5, ml: 5, mt: 3, width: '25ch' } }}
                                />
                                <TextField
                                    label="ชื่อ-สกุลนักศึกษาคนที่ 2"
                                    value={projectSelect?.SName2 || ''}
                                    disabled
                                    sx={{ '& > :not(style)': { mt: 3, width: '30ch' } }}
                                />
                                <p>อาจารย์ที่ปรึกษา</p>
                                <TextField
                                    label="ชื่ออาจารย์ที่ปรึกษา"
                                    value={projectSelect?.TName || ''}
                                    disabled
                                    sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                />
                            </Box>

                            {projectSelect && (
                                <>
                                    <h2>ตารางลงคะแนนสำหรับกรรมการสอบ</h2>
                                    <TableContainer component={Paper}>
                                        <Table>
                                            <TableHead>
                                                <TableRow>
                                                    <TableCell align="center">คะแนนเต็ม</TableCell>
                                                    <TableCell align="center">คะแนนที่ได้</TableCell>
                                                    <TableCell align="center">Actions</TableCell>
                                                </TableRow>
                                            </TableHead>
                                            <TableBody>
                                                {data.map((row) => (
                                                    <TableRow key={row.id}>
                                                        <TableCell>{row.name}</TableCell>
                                                        <TableCell align="center">{row.fullscores}</TableCell>
                                                        <TableCell align="center">
                                                            {editingRowId === row.id ? (
                                                                <TextField
                                                                    type="number"
                                                                    value={row.score}
                                                                    onChange={(e) => handleNameChange(row.id, e.target.value)}
                                                                />
                                                            ) : (
                                                                row.score
                                                            )}
                                                        </TableCell>
                                                        <TableCell align="center">
                                                            {editingRowId === row.id ? (
                                                                <>
                                                                    <Button onClick={handleSaveClick}>Save</Button>
                                                                    <Button onClick={() => setEditingRowId(null)}>Cancel</Button>
                                                                </>
                                                            ) : (
                                                                <Button onClick={() => handleEditClick(row.id)}>Edit</Button>
                                                            )}
                                                        </TableCell>
                                                    </TableRow>
                                                ))}
                                            </TableBody>
                                        </Table>
                                    </TableContainer>
                                    <Button onClick={handleSubmit} variant="contained" color="primary">
                                        Submit
                                    </Button>
                                </>
                            )}
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>

            {/* Success Dialog */}
            <Dialog
                open={openDialog}
                onClose={handleCloseDialog}
            >
                <DialogTitle>Success</DialogTitle>
                <DialogContent>
                    <p>Subject has been added successfully!</p>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Close</Button>
                </DialogActions>
            </Dialog>
        </MainCard>
    );
}

