import React, { useState, useEffect } from 'react';
import { Table,Typography, TableBody, TableCell, TableContainer, TableHead, TableRow, Button, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import { Stack } from '@mui/system';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import axios from 'axios';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';


function InputScoreCSB03() {

  const [Data, setData] = useState([
    { id: 1, name: 'การออกแบบหรือแนวคิด', fullscores: '10', score: '' },
    { id: 2, name: 'วิธีการ/การดำเนินงาน', fullscores: '20', score: '' },
    { id: 3, name: 'ความสมบูรณ์ของผลงาน', fullscores: '20', score: '' },
    { id: 4, name: 'เนื้อหาและรูปแบบของปริญญานิพนธ์', fullscores: '10', score: '' },
    { id: 5, name: 'การนำเสนอโครงงาน', fullscores: '10', score: '' },
    { id: 6, name: 'การนำผลงานไปใช้ประโยชน์', fullscores: '5', score: '' },
    { id: 7, name: 'สรุป/วิจารณ์/การพัฒนาต่อในอนาคต', fullscores: '5', score: '' },
    { id: 8, name: 'คะแนนรวม', fullscores: '80', score: '' }, //ระบบต้องคำนวณคะแนนออกมา
    // Add more data as needed
  ]);
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);

  const [openPopup, setOpenPopup] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);


  const handleLinkClick = (index) => {
    setOpenPopup(true);
    setSelectedRow(index);
  };

  const handleSavePopup = async (e) => {
    if (allChecked) {
      e.preventDefault();
      try {
        await saveScore(); // Save the score
        // Remove only the selected project from the list
        setProjects(prevProjects => prevProjects.filter(project => project.P_id !== selectedProject.P_id));
        resetCheckboxData(); // Reset the form data
        setOpen(false); // Close the popup
      } catch (error) {
        console.error('Error saving score:', error);
      }
    } else {
      alert('กรุณาตรวจสอบให้ครบทุกหัวข้อก่อนทำการบันทึก');
    }
  };

  const handleClosePopup = () => {
    setOpenPopup(false);
  };

  const handleRedRow = (index) => {
    const updatedProjects = projects.map((project, i) =>
      i === index ? { ...project, isRed: true } : project
    );
    setProjects(updatedProjects);
  };

  const handleDisableRow = (index) => {
    const updatedProjects = projects.map((project, i) =>
      i === index ? { ...project, isDisabled: true } : project
    );
    setProjects(updatedProjects);
  };

  const handleDonNotGoAny = () => {
    const updatedData = projects.map(project => ({ ...project, isRed: true, isDisabled: true }));
    setProjects(updatedData);
  };

  const [ProjectSelect2, setProjectSelect2] = useState('');


  const [openDialog, setOpenDialog] = useState(false);

  // Function to handle closing the dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const [openDialog2, setOpenDialog2] = useState(false);

  const handleSubmit2 = (e) => {
    e.preventDefault();
    // Perform any action with the linkValue, such as redirecting to the provided link
    // For example: window.location.href = linkValue;
    setOpenDialog2(true);
  };

  // Function to handle closing the dialog
  const handleCloseDialog2 = () => {
    setOpenDialog2(false);
  };


  const handleChange4 = (id, field, value) => {
    console.log(value);

    const isValidInput = (input) => {
      // Regular Expression สำหรับการตรวจสอบอักษรพิเศษ
      const regex = /^[0-9]*$/; // อนุญาตเฉพาะตัวเลข 0-9

      return regex.test(input);
    };

    const numericValue = Number(value);

    setData(prevData => {
      // อัปเดตข้อมูลที่ถูกกรอกลงในฟิลด์ score
      const updatedData = prevData.map(item => {
        if (item.id === id) {
          // Convert value to a number
          const numericValue = Number(value);
          // Ensure the value is valid
          if (field === 'score') {
            if (!isValidInput(value) || numericValue < 0 || numericValue > Number(item.fullscores) || isNaN(numericValue)) {
              return item; // If not valid, return the item as is
            }
          }

          // Update the specific field
          return { ...item, [field]: value };
        }

        // Keep other items unchanged
        return item;
      });

      // คำนวณคะแนนรวม
      const totalScore = updatedData.reduce((acc, item) => {
        if (item.id !== 7) {
          return acc + Number(item.score || 0); // รวมคะแนนของรายการที่ไม่ใช่ id 7
        }
        return acc;
      }, 0);

      // อัปเดตคะแนนรวมใน id: 7
      return updatedData.map(item => {
        if (item.id === 7) {
          return { ...item, score: totalScore.toString() };
        }
        return item;
      });
    });
  };


  const handleChange5 = (id, field, value) => {
    console.log(value);

    const isValidInput = (input) => {
      // Regular Expression สำหรับการตรวจสอบอักษรพิเศษ
      const regex = /^[0-9]*$/; // อนุญาตเฉพาะตัวเลข 0-9

      return regex.test(input);
    };

    const numericValue = Number(value);

    setData(prevData => {
      // อัปเดตข้อมูลที่ถูกกรอกลงในฟิลด์ score
      const updatedData = prevData.map(item => {
        if (item.id === id) {
          // Convert value to a number
          const numericValue = Number(value);
          // Ensure the value is valid
          if (field === 'score') {
            if (!isValidInput(value) || numericValue < 0 || numericValue > Number(item.fullscores) || isNaN(numericValue)) {
              return item; // If not valid, return the item as is
            }
          }

          // Update the specific field
          return { ...item, [field]: value };
        }

        // Keep other items unchanged
        return item;
      });

      // คำนวณคะแนนรวม
      const totalScore = updatedData.reduce((acc, item) => {
        if (item.id !== 8) {
          return acc + Number(item.score || 0); // รวมคะแนนของรายการที่ไม่ใช่ id 8
        }
        return acc;
      }, 0);

      // อัปเดตคะแนนรวมใน id: 8
      return updatedData.map(item => {
        if (item.id === 8) {
          return { ...item, score: totalScore.toString() };
        }
        return item;
      });
    });
  };


  // const router = useRouter();

  // State for form data
  const [formData, setFormData] = useState({
    Er_name: '',
    Er_results: '',
    Er_com: '',
    Er_status: '',
    Er_date: '',
    Er_time: ''
  });

  // State for subject names fetched from the database
  const [examscores, setExamscores] = useState([]);

  // State for confirmation dialog
  const [open, setOpen] = useState(false);

  useEffect(() => {
    const fetchExamscores = async () => {
      try {
        const response = await axios.get('http://localhost:9999/Exam_results');
        if (response.data && Array.isArray(response.data)) {
          setExamscores(response.data.map(item => item.Er_name));
        }
      } catch (error) {
        console.error('Error fetching subject names:', error);
      }
    };

    fetchExamscores();
  }, []);

  // Handle form input changes
  const handleChange2 = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:9999/Exam_results', formData);
      console.log('Subject added:', response.data);
      alert("Subject added successfully!");
      // Reset form after successful submission
      setFormData({
        Er_name: '',
        Er_results: '',
        Er_com: '',
        Er_status: '',
        Er_date: '',
        Er_time: ''
      });
    } catch (error) {
      console.error('Error adding subject:', error);
      alert("Failed to add subject. Please try again later.");
    }
  };

  // const handleConfirm = (e) => {
  //   handleSubmit(e);
  //   handleClosePopup();
  // };

  // // return/
  // // <Button onClick={handleConfirm} color="primary" autoFocus>
  // //                             Confirm
  // //                         </Button>

  const [ProjectSelect, setProjectSelect] = useState({
    ID: '',
    Name: '',
    SID: '',
    SName: '',
    SID2: '',
    SName2: '',
    TID: '',
    TName: '',
  });

  // Handle change function to update the selected project
  const handleChange = (event) => {
    const selectedProjectName = event.target.value;
    const selectedProject = project.find((proj) => proj.Name === selectedProjectName);

    // Update the state with the selected project's details
    if (selectedProject) {
      setProjectSelect(selectedProject);
    } else {
      setProjectSelect({
        ID: '',
        Name: '',
        SID: '',
        SName: '',
        SID2: '',
        SName2: '',
        TID: '',
        TName: '',
      });
    }
  };




  return (
    <Paper style={{ padding: 16 }}>
      <Grid container spacing={gridSpacing}>
        <Grid item xs={12}>
          <Grid container direction="column" spacing={1}>
            <h1>ประเมินการสอบหัวข้อโครงงานพิเศษ</h1>
            <Button onClick={handleDonNotGoAny} variant="contained" color="error">
              ไม่เข้าร่วมประเมินทั้งหมด
            </Button>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ลำดับที่</TableCell>
                    <TableCell>ชื่อโครงงาน</TableCell>
                    <TableCell>ประเมินการสอบโครงงานพิเศษ</TableCell>
                    <TableCell>ไม่ประสงค์ลงคะแนนสอบ</TableCell>
                    <TableCell>ไม่เข้าประเมินการสอบ</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {projects.map((project, index) => (
                    <TableRow key={project.P_id}>
                      <TableCell>{index + 1}</TableCell>
                      <TableCell>{project.P_name}</TableCell>
                      <TableCell>
                        <Button
                          onClick={() => handleLinkClick(index)}
                          variant="outlined"
                          disabled={project.isDisabled}
                        >
                          ประเมิน
                        </Button>
                      </TableCell>
                      <TableCell>
                        <Button
                          onClick={() => handleDisableRow(index)}
                          variant="outlined"
                          disabled={project.isDisabled || project.isRed}
                        >
                          ไม่ประสงค์ลงคะแนน
                        </Button>
                      </TableCell>
                      <TableCell>
                        <Button
                          onClick={() => handleRedRow(index)}
                          variant="outlined"
                          disabled={project.isDisabled || project.isRed}
                        >
                          ไม่เข้าประเมิน
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Grid>
        </Grid>
      </Grid>

      {selectedProject && (
        <Dialog 
          open={open} 
          onClose={handleClose} 
          maxWidth="lg" 
          fullWidth 
          PaperProps={{
            style: {
              minHeight: '80vh', 
              maxHeight: '90vh',
            }
          }}
        >
          <DialogTitle>รายละเอียดโครงงาน</DialogTitle>
          <DialogContent>
            <Typography variant="h3">ชื่อโครงงาน: {selectedProject.P_name}</Typography>
            <Typography>นักเรียนคนที่ 1: {selectedProject.P_S1}</Typography>
            <Typography>นักเรียนคนที่ 2: {selectedProject.P_S2}</Typography>
            <Typography>ที่ปรึกษา: {selectedProject.P_T}</Typography>
            <h2>ตารางลงคะแนนสำหรับกรรมการสอบ</h2>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ลำดับที่</TableCell>
                    <TableCell>เกณฑ์พิจารณา</TableCell>
                    <TableCell>คำอธิบาย</TableCell>
                    <TableCell>ผลการประเมิน</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {Data.map((item, index) => (
                    <TableRow key={item.id}>
                      <TableCell>{item.id}</TableCell>
                      <TableCell>{item.name}</TableCell>
                      <TableCell>
                        <ul>
                          {(item.description || []).map((desc, idx) => (
                            <li key={idx}>{desc}</li>
                          ))}
                        </ul>
                      </TableCell>
                      <TableCell>
                        {item.id === 8 ? (
                          <TextField value={item.score} disabled fullWidth margin="none" />
                        ) : (
                          <div>
                            <label htmlFor={`pass-${index}`}>
                              <input
                                type="radio"
                                id={`pass-${index}`}
                                checked={item.score === "ผ่าน"}
                                onChange={() => handleCheckboxChange(item.id, "yes")}
                              />
                              ผ่าน
                            </label>
                            <label htmlFor={`fail-${index}`}>
                              <input
                                type="radio"
                                id={`fail-${index}`}
                                checked={item.score === "ไม่ผ่าน"}
                                onChange={() => handleCheckboxChange(item.id, "no")}
                              />
                              ไม่ผ่าน
                            </label>
                          </div>
                        )}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </DialogContent>

          <DialogActions>
            <Button onClick={handleSavePopup} color="primary" disabled={!allChecked}>บันทึก</Button>
            <Button onClick={handleClose} color="primary">ปิด</Button>
          </DialogActions>
        </Dialog>
      )}
    </Paper>
  );
}

export default InputScoreCSB03;
