import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Grid,
    Box,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    TextField,
    Button,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    Stack
} from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';

const ChairmanScoreCSB02 = () => {
    const [projects, setProjects] = useState([]);
    const [selectedProject, setSelectedProject] = useState(null);
    const [data, setData] = useState([{ id: 1, name: 'คะแนนรวม', fullscores: '90', score: '', Er_CSB02_Ch: '' }]);
    const [editingRowId, setEditingRowId] = useState(null);

    const fetchProjects = async () => {
        try {
            const projectResponse = await axios.get('http://localhost:9999/Project');
            const examResponse = await axios.get('http://localhost:9999/Exam_results');

            const filteredProjects = projectResponse.data.filter(project => {
                const result = examResponse.data.find(result => result.Er_Pname === project.P_name);
                return result?.Er_CSB02 !== '';
            });
            setProjects(filteredProjects);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    useEffect(() => {
        fetchProjects();
    }, []);

    useEffect(() => {
        const fetchExamResults = async () => {
            if (selectedProject) {
                try {
                    const examResponse = await axios.get('http://localhost:9999/Exam_results');
                    const projectExamResult = examResponse.data.find(result => result.Er_Pname === selectedProject.P_name);
                    if (projectExamResult) {
                        setData([{ id: 1, name: 'คะแนนรวม', fullscores: '90', score: projectExamResult.Er_CSB02, Er_CSB02_Ch: projectExamResult.Er_CSB02_Ch || '' }]);
                    } else {
                        setData([{ id: 1, name: 'คะแนนรวม', fullscores: '90', score: '', Er_CSB02_Ch: '' }]);
                    }
                } catch (error) {
                    console.error('Error fetching exam results:', error);
                }
            }
        };

        fetchExamResults();
    }, [selectedProject]);

    const handleChange = (event) => {
        const selectedProject = projects.find(p => p.P_name === event.target.value);
        setSelectedProject(selectedProject);
    };

    const handleNameChange = (value) => {
        if (/^\d*$/.test(value) && Number(value) <= Number(data[0].fullscores)) {
            setData(prevData => prevData.map(item =>
                item.id === 1 ? { ...item, score: value } : item
            ));
        }
    };

    const handleEditClick = () => {
        setEditingRowId(1);
    };

    const handleSaveClick = () => {
        setEditingRowId(null);
    };

    const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedProject || !selectedProject.P_name) {
        alert("กรุณาเลือกโครงงานเพื่อแก้ไข.");
        return;
    }

    try {
        // ค้นหาข้อมูลโครงงานที่ตรงกับชื่อโครงงานที่เลือก
        const currentDataResponse = await axios.get(`http://localhost:9999/Exam_results?Er_Pname=${selectedProject.P_name}`);
        const currentData = currentDataResponse.data[0]; // ใช้ข้อมูลตัวแรกจากผลลัพธ์

        if (!currentData || !currentData._id) {
            alert("ไม่พบข้อมูลสำหรับโครงงานที่เลือก กรุณาตรวจสอบชื่อโครงงาน.");
            return;
        }

        // รับคะแนนที่แก้ไขใหม่จากฟอร์ม
        const updatedEr_CSB02 = data.find(item => item.name === 'คะแนนรวม')?.score || '';

        // ตรวจสอบให้แน่ใจว่าแก้ไขโครงงานที่เลือก (เช่น rty) เท่านั้น
        if (currentData.Er_Pname === selectedProject.P_name) {
            // PUT request เพื่ออัปเดต Er_CSB02
            const updatePayload = {
                Er_CSB02: updatedEr_CSB02,
                Er_CSB02_Ch: 'ผ่าน' // อัปเดตสถานะเป็น 'ผ่าน'
            };

            const updateResponse = await axios.put(`http://localhost:9999/Exam_results/${currentData._id}`, updatePayload);

            if (updateResponse.status === 200) {
                alert("อัปเดตคะแนนโครงงานและสถานะเรียบร้อยแล้ว.");

                // เคลียร์ข้อมูลที่เลือกและรีเซ็ตฟอร์มหลังจากอัปเดตสำเร็จ
                setSelectedProject(null);
                setData([{ id: 1, name: 'คะแนนรวม', fullscores: '90', score: '', Er_CSB02_Ch: '' }]);

                // รีเฟรชรายชื่อโครงงานและลบโครงงานที่อัปเดตแล้ว
                fetchProjects().then(() => {
                    setProjects(prevProjects => prevProjects.filter(project => project.P_name !== selectedProject.P_name));
                });
            } else {
                alert("การอัปเดตไม่สำเร็จ กรุณาลองใหม่.");
            }
        } else {
            alert("ไม่พบข้อมูลโครงงานที่ตรงกับชื่อที่เลือก.");
        }

    } catch (error) {
        console.error('เกิดข้อผิดพลาดในการอัปเดตโครงงาน:', error);
        alert("เกิดข้อผิดพลาด กรุณาลองใหม่อีกครั้ง.");
    }
};


    return (
        <MainCard>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Grid container direction="column" spacing={1}>
                                <Box fontSize='18px' sx={{ marginTop: 5 }}>
                                    <h1>แบบประเมินโครงงานพิเศษ 1 (สอบก้าวหน้า)</h1>
                                    <p>เลือกชื่อโครงงาน</p>
                                    <FormControl>
                                        <InputLabel id="ProjectName-select-label">ชื่อโครงงาน</InputLabel>
                                        <Select
                                            labelId="ProjectName-select-label"
                                            value={selectedProject?.P_name || ''}
                                            onChange={handleChange}
                                            margin="normal"
                                            sx={{ minWidth: 150 }}
                                        >
                                            {projects.map((p) => (
                                                <MenuItem key={p._id} value={p.P_name}>
                                                    {p.P_name}
                                                </MenuItem>
                                            ))}
                                        </Select>
                                    </FormControl>
                                    <br />
                                    <p>โดย</p>
                                    <TextField
                                        label="ชื่อ-สกุลนักศึกษาคนที่ 1"
                                        value={selectedProject?.P_S1 || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                    />
                                    <TextField
                                        label="ชื่อ-สกุลนักศึกษาคนที่ 2"
                                        value={selectedProject?.P_S2 || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                    />
                                    <p>อาจารย์ที่ปรึกษา</p>
                                    <TextField
                                        label="ชื่ออาจารย์ที่ปรึกษา"
                                        value={selectedProject?.P_T || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '40ch' } }}
                                    />
                                </Box>
                                {selectedProject && (
                                    <>
                                        <h2>ตารางคะแนนสำหรับประธานกรรมการสอบ</h2>
                                        <TableContainer component={Paper}>
                                            <Table>
                                                <TableHead>
                                                    <TableRow>
                                                        <TableCell>คะแนนเต็ม</TableCell>
                                                        <TableCell>คะแนนได้</TableCell>
                                                        <TableCell>แก้ไข</TableCell>
                                                    </TableRow>
                                                </TableHead>
                                                <TableBody>
                                                    {data.map((item) => (
                                                        <TableRow key={item.id}>
                                                            <TableCell>{item.fullscores}</TableCell>
                                                            <TableCell>
                                                                {editingRowId === item.id ? (
                                                                    <TextField
                                                                        value={item.score}
                                                                        onChange={(e) => handleNameChange(e.target.value)}
                                                                        inputProps={{
                                                                            inputMode: 'numeric',
                                                                            pattern: '[0-9]*'
                                                                        }}
                                                                    />
                                                                ) : (
                                                                    item.score
                                                                )}
                                                            </TableCell>
                                                            <TableCell>
                                                                {editingRowId === item.id ? (
                                                                    <Button variant="contained" onClick={handleSaveClick}>
                                                                        บันทึก
                                                                    </Button>
                                                                ) : (
                                                                    <Button variant="contained" onClick={handleEditClick}>
                                                                        แก้ไข
                                                                    </Button>
                                                                )}
                                                            </TableCell>
                                                        </TableRow>
                                                    ))}
                                                </TableBody>
                                            </Table>
                                        </TableContainer>
                                    </>
                                )}
                                <div>
                                    <form onSubmit={handleSubmit}>
                                        <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
                                            <Button variant="contained" color="primary" type="submit">
                                                บันทึก
                                            </Button>
                                            <Button variant="contained" color="secondary" onClick={() => setSelectedProject(null)}>
                                                ล้างข้อมูล
                                            </Button>
                                        </Stack>
                                    </form>
                                </div>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
};

export default ChairmanScoreCSB02;
