import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import { Stack } from '@mui/system';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';

function ChairmanScoreCSB02() {
    const [project, setProject] = useState([]);
    const [projectSelect, setProjectSelect] = useState(null);
    const [data, setData] = useState([
        { id: 1, name: 'คะแนนรวม', fullscores: '90', score: '' },
    ]);
    const [editingRowId, setEditingRowId] = useState(null);

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const projectResponse = await axios.get('http://localhost:9999/Project');
                if (projectResponse.data && Array.isArray(projectResponse.data)) {
                    setProject(projectResponse.data);
                }

                // Fetch exam results
                const examResponse = await axios.get('http://localhost:9999/Exam_results');
                if (examResponse.data && Array.isArray(examResponse.data)) {
                    // Update with specific project results if needed
                    const projectExamResult = examResponse.data.find(result => result.Er_Pname === projectSelect?.P_name);
                    if (projectExamResult) {
                        setData(prevData => prevData.map(item =>
                            item.id === 1 ? { ...item, score: projectExamResult.Er_CSB02 } : item
                        ));
                    }
                }
            } catch (error) {
                console.error('Error fetching project or exam data:', error);
            }
        };

        fetchProjects();
    }, [projectSelect]);


    const handleChange = async (event) => {
        const selectedProject = project.find(p => p.P_name === event.target.value);
        setProjectSelect(selectedProject);
        if (selectedProject) {
            // Fetch exam results for the selected project
            try {
                const examResponse = await axios.get('http://localhost:9999/Exam_results');
                if (examResponse.data && Array.isArray(examResponse.data)) {
                    const projectExamResult = examResponse.data.find(result => result.Er_Pname === selectedProject.P_name);
                    if (projectExamResult) {
                        setData(prevData => [
                            { id: 1, name: 'คะแนนรวม', fullscores: '90', score: projectExamResult.Er_CSB02 }
                        ]);
                    }
                }
            } catch (error) {
                console.error('Error fetching exam results:', error);
            }
        }
    };


    const handleNameChange = (value) => {
        if (/^\d*$/.test(value) && Number(value) <= Number(data[0].fullscores)) {
            setData(prevData =>
                prevData.map(item =>
                    item.id === 1 ? { ...item, score: value } : item
                )
            );
        }
    };

    const handleEditClick = () => {
        setEditingRowId(1);
    };

    const handleSaveClick = () => {
        setEditingRowId(null);

    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const updatedEr_CSB02 = data.find(item => item.name === 'คะแนนรวม').score;

            const response = await axios.put('http://localhost:9999/Exam_results', {
                Er_Pname: projectSelect?.P_name,
                Er_CSB01: '', // Update if needed
                Er_CSB02: updatedEr_CSB02, // Use the updated value here
                Er_CSB03: '', // Update if needed
                Er_CSB01_status: '', // Update if needed
                Er_CSB02_status: '', // Update if needed
                Er_CSB03_status: ''  // Update if needed
            });

            console.log('Subject updated:', response.data);
            alert("Subject updated successfully!");
        } catch (error) {
            console.error('Error updating subject:', error);
            alert("Failed to update subject. Please try again later.");
        }
    };

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Grid container direction="column" spacing={1}>
                                <div>
                                    <Box fontSize='18px' sx={{ marginTop: 5 }}>
                                        <h1>แบบประเมินโครงงานพิเศษ 1 (สอบก้าวหน้า)</h1>
                                        <p>เลือกชื่อโครงงาน</p>
                                        <FormControl>
                                            <InputLabel id="ProjectName-select-label">ชื่อโครงงาน</InputLabel>
                                            <Select
                                                labelId="ProjectName-select-label"
                                                value={projectSelect?.P_name || ''}
                                                onChange={handleChange}
                                                margin="normal"
                                                sx={{ minWidth: 150 }}
                                            >
                                                {project.map((p) => (
                                                    <MenuItem
                                                        key={p.P_id}
                                                        value={p.P_name}
                                                    >
                                                        {p.P_name}
                                                    </MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                        <br />
                                        <p>โดย</p>
                                        <TextField
                                            label="ชื่อ-สกุลนักศึกษาคนที่ 1"
                                            value={projectSelect?.P_S1 || ''}
                                            disabled
                                            sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                        />
                                        <TextField
                                            label="ชื่อ-สกุลนักศึกษาคนที่ 2"
                                            value={projectSelect?.P_S2 || ''}
                                            disabled
                                            sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                        />
                                        <p>อาจารย์ที่ปรึกษา</p>
                                        <TextField
                                            label="ชื่ออาจารย์ที่ปรึกษา"
                                            value={projectSelect?.P_T || ''}
                                            disabled
                                            sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '40ch' } }}
                                        />
                                    </Box>
                                    {projectSelect && (
                                        <>
                                            <h2>ตารางคะแนนสำหรับประธานกรรมการสอบ</h2>
                                            <TableContainer component={Paper}>
                                                <Table>
                                                    <TableHead>
                                                        <TableRow>
                                                            <TableCell>คะแนนเต็ม</TableCell>
                                                            <TableCell>คะแนนได้</TableCell>
                                                            <TableCell>แก้ไข</TableCell>
                                                        </TableRow>
                                                    </TableHead>
                                                    <TableBody>
                                                        {data.map((item) => (
                                                            <TableRow key={item.id}>
                                                                <TableCell>{item.fullscores}</TableCell>
                                                                <TableCell>
                                                                    {editingRowId === item.id ? (
                                                                        <TextField
                                                                            value={item.score}
                                                                            onChange={(e) => handleNameChange(e.target.value)}
                                                                            inputProps={{
                                                                                inputMode: 'numeric',
                                                                                pattern: '[0-9]*'
                                                                            }}
                                                                        />
                                                                    ) : (
                                                                        item.score
                                                                    )}
                                                                </TableCell>
                                                                <TableCell>
                                                                    {editingRowId === item.id ? (
                                                                        <Button variant="contained" onClick={handleSaveClick}>
                                                                            บันทึก
                                                                        </Button>
                                                                    ) : (
                                                                        <Button variant="contained" onClick={handleEditClick}>
                                                                            แก้ไข
                                                                        </Button>
                                                                    )}
                                                                </TableCell>
                                                            </TableRow>
                                                        ))}
                                                    </TableBody>

                                                </Table>
                                            </TableContainer>
                                        </>
                                    )}
                                    <div>
                                        <form onSubmit={handleSubmit}>
                                            <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
                                                <Button variant="contained" color="primary" type="submit">
                                                    บันทึก
                                                </Button>
                                                <Button variant="contained" color="secondary" onClick={() => setOpenDialog(false)}>
                                                    ยกเลิก
                                                </Button>
                                            </Stack>
                                        </form>
                                    </div>
                                </div>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
}

