import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, Grid, Typography, Paper } from '@mui/material';
import ExamSelect from './ExamSelect';

const ManageExamRoom = () => {
  const [examRooms, setExamRooms] = useState([]);
  const [newRoom, setNewRoom] = useState('');
  const [selectedExam, setSelectedExam] = useState('');

  useEffect(() => {
    // Fetch existing exam rooms
    axios.get('http://localhost:9999/exam-rooms')
      .then(response => {
        setExamRooms(response.data);
      })
      .catch(error => {
        console.error('Error fetching exam rooms:', error);
      });
  }, []);

  const handleAddRoom = () => {
    if (newRoom) {
      axios.post('http://localhost:9999/exam-rooms', { name: newRoom })
        .then(response => {
          setExamRooms([...examRooms, response.data]);
          setNewRoom('');
        })
        .catch(error => {
          console.error('Error adding exam room:', error);
        });
    }
  };

  const handleDeleteRoom = (id) => {
    axios.delete(`http://localhost:9999/exam-rooms/${id}`)
      .then(() => {
        setExamRooms(examRooms.filter(room => room.id !== id));
      })
      .catch(error => {
        console.error('Error deleting exam room:', error);
      });
  };

  const handleExamSelect = (examId) => {
    setSelectedExam(examId);
    console.log('Selected Exam ID:', examId);
  };

  return (
    <Paper style={{ padding: 16 }}>
      <Typography variant="h4" gutterBottom>
        จัดห้องสอบ
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} md={6}>
          <ExamSelect onSelect={handleExamSelect} />
        </Grid>
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="New Exam Room"
            value={newRoom}
            onChange={(e) => setNewRoom(e.target.value)}
          />
          <Button variant="contained" color="primary" onClick={handleAddRoom}>
            Add Room
          </Button>
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h6" gutterBottom>
            Existing Exam Rooms
          </Typography>
          <ul>
            {examRooms.map(room => (
              <li key={room.id}>
                {room.name}
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => handleDeleteRoom(room.id)}
                >
                  Delete
                </Button>
              </li>
            ))}
          </ul>
        </Grid>
      </Grid>
    </Paper>
  );
};

export default ManageExamRoom;
