import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import TableContainer from '@mui/material/TableContainer';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Dialog from '@mui/material/Dialog';
import { Stack } from '@mui/system';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';
import axios from 'axios';

function DepartmentHeadScoreCSB03() {
    const [ProjectSelect, setProjectSelect] = useState({});
    const [openDialog, setOpenDialog] = useState(false);
    const [projects, setProjects] = useState([]);
    const [data, setData] = useState([{ id: 1, name: 'คะแนนรวม', fullscores: '80', score: '' }]);

    // Fetch project names from the API
    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const response = await axios.get('http://localhost:9999/Exam_results');
                setProjects(response.data); // Assuming response.data is an array of projects
            } catch (error) {
                console.error('Error fetching projects:', error);
            }
        };

    });

    const fetchProjectDetails = async (projectName) => {
        try {
            const projectResponse = await axios.get('http://localhost:9999/Project');
            const projectDetail = projectResponse.data.find(p => p.P_name === projectName);
            setProjectDetails(projectDetail);
        } catch (error) {
            console.error('Error fetching project details:', error);
        }
    };

    useEffect(() => {
        fetchProjects();
    }, []);


    useEffect(() => {
        const fetchExamResults = async () => {
            if (selectedProject) {
                try {
                    const examResponse = await axios.get('http://localhost:9999/Exam_results');
                    const projectExamResult = examResponse.data.find(result => result.Er_Pname === selectedProject.Er_Pname);
                    if (projectExamResult) {
                        setData([{ id: 1, name: 'คะแนนรวม', fullscores: '90', score: projectExamResult.Er_CSB02 }]);
                    } else {
                        setData([{ id: 1, name: 'คะแนนรวม', fullscores: '90', score: '' }]);
                    }

                    // Fetch project details (students and advisor) from Project API
                    fetchProjectDetails(selectedProject.Er_Pname);
                } catch (error) {
                    console.error('Error fetching exam results:', error);
                }
            }
        };

        fetchExamResults();
    }, [selectedProject]);

    // Function to handle project selection change
    const handleChange = (event) => {
        const selectedProject = projects.find(p => p.Er_Pname === event.target.value);
        setSelectedProject(selectedProject);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Submit form logic (existing code)
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
    };

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <div>
                                <Box fontSize='18px' sx={{ marginTop: 5 }}>
                                    <h1>แบบประเมินโครงงานพิเศษ 2 (ปริญญานิพนธ์)</h1>
                                    <p>ชื่อโครงงาน</p>
                                    <FormControl>
                                        <InputLabel id="ProjectName-select-label">ชื่อโครงงาน</InputLabel>
                                        <Select
                                            labelId="ProjectName-select-label"
                                            value={ProjectSelect.Name || ''}
                                            onChange={handleChange}
                                            label="ชื่อโครงงาน"
                                            margin="normal"
                                            sx={{ minWidth: 150 }}
                                        >
                                            {projects.map((project) => (
                                                <MenuItem key={project.ID} value={project.Name}>
                                                    {project.Name}
                                                </MenuItem>
                                            ))}
                                        </Select>
                                    </FormControl>
                                </Box>
                                {/* Table for entering scores */}
                                <h2>ตารางลงคะแนนสำหรับกรรมการสอบ</h2>
                                <TableContainer component={Paper}>
                                    <Table>
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>คะแนนเต็ม</TableCell>
                                                <TableCell>คะแนนได้</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {/* Map through data to render table rows */}
                                        </TableBody>
                                    </Table>
                                    <Dialog open={openDialog} onClose={handleCloseDialog}>
                                        <DialogTitle>ทำการบันทึกสำเร็จ !!</DialogTitle>
                                        <DialogContent>
                                            <p>รอการตรวจสอบจากเจ้าหน้าที่</p>
                                        </DialogContent>
                                        <DialogActions>
                                            <Button onClick={handleCloseDialog}>Close</Button>
                                        </DialogActions>
                                    </Dialog>
                                </TableContainer>
                                <Stack alignItems="center" justifyContent="center" fontSize='18px' sx={{ marginTop: 3 }}>
                                    <Button variant="contained" onClick={handleSubmit}>
                                        อนุมัติคะแนน
                                    </Button>
                                </Stack>
                            </div>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
}

export default DepartmentHeadScoreCSB03;
