import React, { useState, useEffect } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Paper,
  Grid,
} from '@mui/material';
import axios from 'axios';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';

const SpecialProjectEvaluation = () => {
  const [projects, setProjects] = useState([]); // State for storing project data from API
  const [openPopup, setOpenPopup] = useState(false); // State for managing the popup
  const [selectedProject, setSelectedProject] = useState(null); // State for the selected project

  const [Data, setData] = useState([
    {
      id: 1, name: 'การวิเคราะห์ปัญหา(Problem Analysis)', description: [
        '1. มีการระบุที่มาของปัญหา',
        '2. มีการนำเสนอข้อมูลทางสถิติหรือหลักฐานเพื่อสนับสนุนความสำคัญของปัญหา',
        '3. มีอ้างอิงแหล่งข้อมูลที่น่าเชื่อถือ']
      , score: ''
    },
    {
      id: 2, name: 'แนวคิด/ แนวทางของระบบ(Solution)', description: [
        '1. มีความสามารถแก้ปัญหาได้แท้จริง ตรงเป้า',
        '2. มีวิธีการที่นำเสนอที่มีประสิทธิภาพ',]
      , score: ''
    },
    {
      id: 3, name: 'ขอบเขตและปริมาณงาน(Scope)', description: [
        '1. มีลักษณะและปริมาณงานเหมาะสมสำหรับปริญญานิพนธ์',
        '2. มีความเหมาะสมตามกรอบเวลา']
      , score: ''
    },
    {
      id: 4, name: 'ต้นแบบระบบ(System Prototype)', description: [
        'มีการนำเสนอต้นแบบระบบด้วยเครื่องมือที่มีความเหมาะสม เช่น Mockup, wireframe, story board และ system architecture',]
      , score: ''
    },
    {
      id: 5, name: 'ลักษณะของกลุ่มผู้ใช้งาน(Target market)', description: [
        '1. มีการกำหนดเป้าหมายสำหรับการใช้งานระบบอย่างชัดเจน',
        '2. มีการศึกษาลักษณะกลุ่มเป้าหมาย',
        '3. มีการศึกษาผลกระทบที่คาดว่าจะเกิดต่อกลุ่มเป้าหมาย']
      , score: ''
    },
    {
      id: 6, name: 'การวิเคราะห์การตลาด (Market analysis)', description: [
        '1. มีการนำเสนอระบบที่ใกล้เคียง/ที่เกี่ยวข้องอย่างน้อย 2 ระบบ',
        '2. มีการระบุข้อจำกัดของระบบที่ใกล้เคียงหรือเกี่ยวข้อง',
        '3. มีการระบุความแตกต่างหรือจุดเด่นของระบบที่นำเสนอ']
      , score: ''
    },
    {
      id: 7, name: 'การศึกษาความเป็นไปได้ (Feasibility study)'
      , description: [
        '1. มีการนำเสนอการศึกษาเทคนิคที่เกี่ยวข้องเบื้องต้น ',
        '2. มีการศึกษาข้อจำกัดของอุปกรณ์/ระบบที่เกี่ยวข้อง']
      , score: ''
    },
    { id: 8, name: 'ผลการประเมิน', score: '' },
  ]);

  useEffect(() => {
    // Fetch project data from API when the component loads
    axios
      .get('http://localhost:9999/Project')
      .then((response) => {
        setProjects(response.data); // Set project data in state
      })
      .catch((error) => {
        console.error('Error fetching projects:', error);
      });
  }, []);


  const handleCheckboxChange = (id, type) => {
    setData(prevData =>
      prevData.map(item =>
        item.id === id
          ? { ...item, score: type === 'yes' ? 'ผ่าน' : 'ไม่ผ่าน' }
          : item
      )
    );
  };
  Data.forEach(function (element) {
    console.log(element.score);
  });

  const updateFinalScore = () => {
    const countPass = Data
      .filter(item => item.id >= 1 && item.id <= 7)
      .filter(item => item.score === 'ผ่าน').length;

    const countFail = Data
      .filter(item => item.id >= 1 && item.id <= 7)
      .filter(item => item.score === 'ไม่ผ่าน').length;

    if (countFail >= 4) {
      setData(prevData =>
        prevData.map(item =>
          item.id === 8
            ? { ...item, score: 'ไม่ผ่านการประเมินหัวข้อ' }
            : item
        )
      );
    } else if (countPass >= 4) {
      setData(prevData =>
        prevData.map(item =>
          item.id === 8
            ? { ...item, score: 'ผ่านการประเมินหัวข้อ' }
            : item
        )
      );
    }
  };
  useEffect(() => {
    updateFinalScore();
  }, [Data]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEvaluation({ ...evaluation, [name]: value });
  };

  const handleOpenPopup = (project) => {
    setSelectedProject(project); // Set selected project to evaluate
    setOpenPopup(true); // Open the evaluation popup
  };

  const handleClosePopup = () => {
    setOpenPopup(false); // Close the popup
  };

  const handleSubmitEvaluation = (e) => {
    e.preventDefault();
    console.log('Submitting Evaluation:', evaluation, 'for project:', selectedProject);
    alert('การประเมินถูกส่งเรียบร้อยแล้ว');
    setOpenPopup(false); // Close the popup after submission
  };

  const handleDonNotGoAny = () => {
    alert('การดำเนินการไม่เข้าร่วมประเมินทั้งหมด');
  };

  const [formData, setFormData] = useState({
    Er_name: '',
    Er_results: '',
    Er_com: '',
    Er_status: '',
    Er_date: '',
    Er_time: ''
  });

  useEffect(() => {
    const fetchExamscores = async () => {
      try {
        const response = await axios.get('http://localhost:9999/Exam_results');
        if (response.data && Array.isArray(response.data)) {
          setExamscores(response.data.map(item => item.Er_name));
        }
      } catch (error) {
        console.error('Error fetching subject names:', error);
      }
    };

    fetchExamscores();
  }, []);
  
  const handleChange2 = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:9999/Exam_results', formData);
      console.log('Subject added:', response.data);
      alert("Subject added successfully!");
      // Reset form after successful submission
      setFormData({
        Er_name: '',
        Er_results: '',
        Er_com: '',
        Er_status: '',
        Er_date: '',
        Er_time: ''
      });
    } catch (error) {
      console.error('Error adding subject:', error);
      alert("Failed to add subject. Please try again later.");
    }
  };


  return (
    <MainCard>
      <Grid container spacing={gridSpacing}>
        <Grid item xs={12}>
          <h1>ประเมินการสอบหัวข้อโครงงานพิเศษ</h1>
          <Button onClick={handleDonNotGoAny} variant="contained" color="error">
            ไม่เข้าร่วมประเมินทั้งหมด
          </Button>

          <TableContainer component={Paper} style={{ marginTop: '20px' }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>ลำดับที่</TableCell>
                  <TableCell>ชื่อโครงงาน</TableCell>
                  <TableCell>ประเมินการสอบโครงงานพิเศษ</TableCell>
                  <TableCell>ไม่ประสงค์ลงคะแนนสอบ</TableCell>
                  <TableCell>ไม่เข้าประเมินการสอบ</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {projects.map((project, index) => (
                  <TableRow key={project.P_id}>
                    <TableCell>{index + 1}</TableCell>
                    <TableCell>{project.P_name}</TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleOpenPopup(project)}
                      >
                        ประเมิน
                      </Button>
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="secondary"
                        onClick={() => alert(`ไม่ประสงค์ลงคะแนนสอบสำหรับโครงงาน: ${project.P_name}`)}
                      >
                        ไม่ประสงค์ลงคะแนนสอบ
                      </Button>
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="error"
                        onClick={() => alert(`ไม่เข้าประเมินการสอบสำหรับโครงงาน: ${project.P_name}`)}
                      >
                        ไม่เข้าประเมินการสอบ
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>

          {/* Evaluation Popup */}
          <Dialog open={openPopup} onClose={handleClosePopup} maxWidth="md" fullWidth>
            <DialogTitle>ประเมินโครงงาน: {selectedProject?.P_name}</DialogTitle>
            <DialogContent>
              {selectedProject && (
                <>
                  <h3>นักศึกษาคนที่ 1: {selectedProject.P_S1}</h3>
                  <h3>นักศึกษาคนที่ 2: {selectedProject.P_S2}</h3>
                  <h3>อาจารย์ที่ปรึกษา: {selectedProject.P_T}</h3>

                  {Data.map((item) => (
                    <div key={item.id} style={{ marginBottom: '16px' }}>
                      <h4>{item.name}</h4>
                      <ul>
                        {item.description.map((desc, index) => (
                          <li key={index}>{desc}</li>
                        ))}
                      </ul>
                      <TextField
                        fullWidth
                        label={`คะแนน ${item.name}`}
                        name={`score-${item.id}`}
                        value={evaluation[`score-${item.id}`] || ''}
                        onChange={handleInputChange}
                        margin="normal"
                        type="number"
                      />
                    </div>
                  ))}
                  <TextField
                    fullWidth
                    label="ความคิดเห็น"
                    name="comments"
                    value={evaluation.comments}
                    onChange={handleInputChange}
                    margin="normal"
                    multiline
                    rows={4}
                  />
                </>
              )}
            </DialogContent>
            <DialogActions>
              <Button onClick={handleClosePopup} color="secondary">
                ยกเลิก
              </Button>
              <Button onClick={handleSubmit} variant="contained" color="primary">
                บันทึกการประเมิน
              </Button>
            </DialogActions>
          </Dialog>
        </Grid>
      </Grid>
    </MainCard>
  );
};

export default SpecialProjectEvaluation;
