import React, { useState, useEffect } from 'react';
import TextField from '@mui/material/TextField';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import { Typography } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';
import { Grid } from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import { styled } from '@mui/material/styles';
import axios from 'axios';

function UploadLinkTranscript1() {
  const [linkValue, setLinkValue] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [Transcript, setTranscript] = useState([]);
  const [uploadedFile, setUploadedFile] = useState(null); // State for the uploaded file
  const [files, setFiles] = useState(Array(3).fill(null));
  const [students, setStudents] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:9999/students");
        setStudents(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleLinkChange = (e) => {
    setLinkValue(e.target.value);
  };

  const handleFileChange = (index) => (e) => {
    const newFiles = [...files];
    newFiles[index] = e.target.files[0];
    setFiles(newFiles);
  };

  const handleSubmit = async () => {
    const user = students.map((student) => student.S_id);
    const name = students.map((student) => student.S_name);

    const formData = new FormData();
    files.forEach((file, index) => {
      if (file) {
        formData.append(`files[]`, file);
      } else {
        alert(`Uploaded file ${index + 1} ไม่ครบ`);
        return;
      }
    });

    formData.append("std", user[0]); // Assuming single user
    formData.append("stdName", name[0]); // Assuming single user

    try {
      const result = await axios.post("http://localhost:9999/files", formData);
      setUploadStatus("success");
      alert("Uploaded Successfully!!!");
      await handleFileUpload(user[0]);
    } catch (error) {
      console.error("Error uploading file: ", error.message);
      alert("Error uploading file. Please try again.");
      setUploadStatus("error");
    }
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const VisuallyHiddenInput = styled('input')({
    clip: 'rect(0 0 0 0)',
    clipPath: 'inset(50%)',
    height: 1,
    overflow: 'hidden',
    position: 'absolute',
    bottom: 0,
    left: 0,
    whiteSpace: 'nowrap',
    width: 1,
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post('http://localhost:9999/FilePDF');
        setTranscript(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleFileUpload = async (fi_id) => {
    try {
      await axios.patch(`http://localhost:9999/files/${fi_id}`, {
        fi_status: "ยังไม่ได้ตรวจสอบ",
      });
    } catch (error) {
      console.error("Error updating file status:", error);
    }
  };

  return (
    <MainCard>
      <Grid container spacing={gridSpacing}>
        <Grid item xs={12}>
          <Grid container alignItems="center" justifyContent="space-between">
            <Grid item>
              <Grid container direction="column" spacing={1}></Grid>
              <Container>
                <h1>ตรวจสอบคุณสมบัติยื่นโครงงานพิเศษ 2 (ปริญญานิพนธ์)</h1>
                <h2>เกณฑ์การประเมิน</h2>
                <p>
                  นักศึกษาโครงการพิเศษสองภาษาต้องลงทะเบียนเรียนวิชา 040613141 Special Project I
                  ได้ผลการเรียนรวม ≥ 102 หน่วยกิต และได้ผลการเรียนรายวิชาภาคฯ 0406xxxxx ≥ 57 หน่วยกิต
                  โดยใช้เอกสารใบรับรองผลการศึกษา (Transcript)
                </p>
                <Box sx={{ display: 'flex', flexDirection: 'column' }}>
                  <Typography
                    sx={{
                      marginTop: 1,
                    }}
                    fontSize={'18px'}
                  >
                    อัปโหลดไฟล์ผลการเรียนจากเว็บไซต์ reg kmutnb
                  </Typography>
                  <Button
                    component="label"
                    role={undefined}
                    variant="contained"
                    tabIndex={-1}
                    startIcon={<CloudUploadIcon />}
                    sx={{ maxWidth: 180, m: 2 }}
                  >
                    Upload file
                    <VisuallyHiddenInput type="file" onChange={handleFileChange} />
                  </Button>

                  {uploadedFile && ( // Display the uploaded file name
                    <Typography variant="body2" sx={{ marginTop: 2 }}>
                      Uploaded file: {uploadedFile.name}
                    </Typography>
                  )}

                  <Grid sx={{ textAlign: 'center' }}>
                    <Button variant="contained" onClick={handleSubmit} sx={{ maxWidth: 100 }}>
                      ตรวจสอบ
                    </Button>
                  </Grid>
                </Box>

                <Dialog open={openDialog} onClose={handleCloseDialog}>
                  <DialogTitle>ตรวจสอบคุณสมบัติยื่นสอบ</DialogTitle>
                  <DialogContent>
                    <p>ผลการตรวจสอบ</p>
                    {uploadedFile && (
                      <Typography variant="body2">
                        File: {uploadedFile.name}
                      </Typography>
                    )}
                  </DialogContent>
                  <DialogActions>
                    <Button onClick={handleCloseDialog}>Close</Button>
                  </DialogActions>
                </Dialog>
              </Container>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </MainCard>
  );
}

export default UploadLinkTranscript1;
