import React, { useState } from "react";
import axios from "axios";
import { Typography, Button, Box, TextField } from '@mui/material';

const FirebaseLogin = ({ onLoginSuccess }) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const onSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:9999/auth/login", {
                username,
                password,
            });

            if (response && response.data) {
                const { api_status, api_message, userInfo } = response.data;

                if (api_status === "success") {
                    console.log("Login successful:", userInfo);
                    
                    // Call the onLoginSuccess prop to navigate
                    onLoginSuccess(); // This line calls the function passed from App component
                } else {
                    setError(api_message);
                }
            }
        } catch (error) {
            console.error("Error logging in:", error);
            setError("Invalid username or password");
        }
    };

    return (
        <Box
            sx={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                height: '100vh',
                backgroundColor: '#f5f5f5',
            }}
        >
            <Box
                sx={{
                    width: 300,
                    padding: 3,
                    backgroundColor: 'white',
                    borderRadius: 2,
                    boxShadow: 3,
                }}
            >
                <Typography variant="h5" gutterBottom align="center">
                    Login
                </Typography>
                <form onSubmit={onSubmit}>
                    <TextField
                        fullWidth
                        label="ICIT Account"
                        variant="outlined"
                        margin="normal"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <TextField
                        fullWidth
                        label="Password"
                        variant="outlined"
                        margin="normal"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <Button
                        fullWidth
                        type="submit"
                        variant="contained"
                        color="primary"
                        sx={{ mt: 2 }}
                    >
                        Sign in
                    </Button>
                    {error && <Typography sx={{ mt: 2, color: 'red' }}>{error}</Typography>}
                </form>
            </Box>
        </Box>
    );
};

export default FirebaseLogin;
