import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Stack } from '@mui/system';
import { Typography } from '@mui/material';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import FormGroup from '@mui/material/FormGroup';
import Button from '@mui/material/Button';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Dialog from '@mui/material/Dialog';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';
import { Value } from 'sass';
import axios from 'axios';

function CSB01() {
    const [StudentSelect, setStudentSelect] = useState('');
    const [StudentSelect2, setStudentSelect2] = useState('');
    const [selectTeacher, setSelectTeacher] = useState('');

    const [selectedValues, setSelectedValues] = useState({
        networks: false,
        graphics: false,
        database: false,
        ai: false,
        games: false,
    });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setSData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleChange2 = (event) => {
        setStudentSelect2(Students.find(person => person.ID === event.target.value));
    };

    const handleSelectTeacherChange = (event) => {
        // const value = event.target.value;
        setSelectTeacher(Teachers.find(person => person.ID === event.target.value));
        console.log(Teachers.find(person => person.ID === event.target.value)); // Log the selected teacher ID
    };

    const handleChangeSelectObject = (event) => {
        const { name, checked } = event.target;

        // Update the state of the corresponding checkbox
        setSelectedValues((prevValues) => ({
            ...prevValues,
            [name]: checked,
        }));

        // Log the updated state
        console.log(`${name} is now ${checked ? 'selected' : 'deselected'}`);
    };

    const [openDialog, setOpenDialog] = useState(false);

    const handleCloseDialog = () => {
        setOpenDialog(false);
    };

    const [selectedValue, setSelectedValue] = useState('');

    const handleRadioChange = (e) => {
        setSelectedValue(e.target.value);
    };

    const handleSelectChange = (e) => {
        const { name, value } = e.target;
        if (name === 'S_id1' || name === 'S_id2') {
            setSData(prevState => ({
                ...prevState,
                [name]: value
            }));
        } else if (name === 'T_id') {
            setTData({ T_id: value });
        }
    };

    // const handleSelectChange = (event) => {
    //     const { name, value } = event.target;
    //     setSData(prevState => ({
    //         ...prevState,
    //         [name]: value
    //     }));
    //     setTData(prevState => ({
    //         ...prevState,
    //         [name]: value
    //     }));

    //     // Update T_name based on selected T_id
    //     if (name === 'T_id') {
    //         const selectedTeacher = teachers.find(teacher => teacher.T_id === value);
    //         setTData(prevState => ({
    //             ...prevState,
    //             T_name: selectedTeacher ? selectedTeacher.T_name : ''
    //         }));
    //     }
    // };

    const [studentData, setStudentData] = useState([]); // เก็บข้อมูลทั้งหมดของนักเรียน (แก้ไขเป็น studentData)
    const [studentNames, setStudentNames] = useState([]); // เก็บเฉพาะรายชื่อนักเรียนสำหรับ Select (แก้ไขเป็น studentNames)
    const [teachers, setTeachers] = useState([]);

    const [PData, setPData] = useState({
        P_name: '',
        P_details: '',
        P_status: '',
        P_CSB01: '',
        P_CSB02: '',
        P_CSB03: '',
        P_S1: '',
                P_S2: '',
                P_T: ''
    });

    const [SData, setSData] = useState({
        S_id: '',
        S_name: '',
        S_email: '',
        S_phone: '',
        S_project: '',
        S_CSB01: '',
        S_CSB02: '',
        S_CSB03: '',
        S_year: '',
        S_match: '',
        S_T_SP1: '',
        S_T_SP2: ''

    });

    const [TData, setTData] = useState({
        T_id: '',
        T_name: '',
        T_email: '',
        T_phone: '',
        T_position: '',
        T_CSB01: '',
        T_CSB02: '',
        T_CSB03: '',
        T_CSB01_point: '',
        T_CSB02_point: '',
        T_CSB03_point: '',

    });

    useEffect(() => {
        const fetchStudent = async () => {
            try {
                const response = await axios.get('http://localhost:9999/students');
                if (response.data && Array.isArray(response.data)) {
                    // กรองข้อมูลซ้ำ
                    const uniqueStudents = Array.from(new Map(response.data.map(item => [item.S_id, item])).values());
                    setStudentData(uniqueStudents); // เก็บข้อมูลทั้งหมดของนักเรียน
                    setStudentNames(uniqueStudents.map(item => ({ S_id: item.S_id, S_name: item.S_name }))); // เก็บเฉพาะ S_id และ S_name
                }
            } catch (error) {
                console.error('Error fetching Student names:', error);
            }
        };
        fetchStudent();
    }, []);

    useEffect(() => {
        const fetchTeachers = async () => {
            try {
                const response = await axios.get('http://localhost:9999/Teacher');
                if (response.data && Array.isArray(response.data)) {
                    setTeachers(response.data);
                }
            } catch (error) {
                console.error('Error fetching Teacher names:', error);
            }
        };
        fetchTeachers();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        // Prepare data to be sent to the server
        const projectData = {
            ...PData,
            P_S1: SData.S_id1,
            P_S2: SData.S_id2,
            P_T: TData.T_id
        };
    
        try {
            const response = await axios.post('http://localhost:9999/Project', projectData);
            console.log('Project added:', response.data);
            alert("Project added successfully!");
            setPData({
                P_name: '',
                P_details: '',
                P_status: '',
                P_CSB01: '',
                P_CSB02: '',
                P_CSB03: '',
                P_S1: '',
                P_S2: '',
                P_T: ''
            });
            setSData({
                S_id1: '',
                S_id2: ''
            });
            setTData({
                T_id: ''
            });
        } catch (error) {
            console.error('Error adding project:', error);
            alert("Failed to add project. Please try again later.");
        }
    
        setOpenDialog(true);
    };

    // const handleSubmit = async (e) => {

    //     try {
    //         const response = await axios.post('http://localhost:9999/Project', PData);
    //         console.log('Project added:', response.data);
    //         alert("Project added successfully!");
    //         setPData({
    //             P_name: '',
    //             P_details: '',
    //             P_status: '',
    //             P_CSB01: '',
    //             P_CSB02: '',
    //             P_CSB03: '',
    //             P_S1: '',
    //             P_S2: '',
    //             P_T: ''
    //         });
    //     } catch (error) {
    //         console.error('Error adding project:', error);
    //         alert("Failed to add project. Please try again later.");
    //     }
    //     setPData({ ...PData, [e.target.id]: e.target.value });
    //     e.preventDefault();
    //     setOpenDialog(true);

    // };

    const [errors, setErrors] = useState({
        projectName: false,
        projectDetails: false,
        atLeastOneCategory: false
    });

    const getFilteredOptions = (excludedIds) => {
        return studentNames.filter(student => !excludedIds.includes(student.S_id));
    };

    const selectedIds = [SData.S_id1, SData.S_id2].filter(id => id);


    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Grid container direction="column" spacing={1}></Grid>
                            <Box
                                component="form"
                                sx={{
                                    marginTop: 8,
                                    display: 'block',
                                    flexDirection: 'column',
                                }}
                                noValidate
                                autoComplete="off"
                            >
                                <Grid container alignItems="center" justifyContent="center" sx={{ ml: 20 }}>
                                    <Grid item>
                                        <Stack alignItems="center" justifyContent="center" spacing={1}>
                                            <Typography fontSize='18px'>
                                                <b>แบบฟอร์มเสนอหัวข้อโครงงานพิเศษ</b><br></br>
                                            </Typography>
                                            <Typography fontSize='18px'>
                                                <b>โครงการพิเศษ (สองภาษา) ภาควิชาวิทยาการคอมพิวเตอร์และสารสนเทศ</b><br></br>
