import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import { Typography, TextField, Dialog, DialogActions, Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';

function Addstudents() {
    const [dialogOpen, setDialogOpen] = useState(false);
    const [dialogMessage, setDialogMessage] = useState('');
    const [studentID, setStudentID] = useState('');  // รหัสนักศึกษา
    const [studentName, setStudentName] = useState('');  // ชื่อนักศึกษา

    const handleConfirm = async () => {
        const studentData = {
            S_id: studentID,   // รหัสนักศึกษา
            S_name: studentName // ชื่อนักศึกษา
        };

        try {
            const response = await fetch('http://localhost:9999/students', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(studentData),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            let message = `รหัสนักศึกษา: ${studentData.S_id}\nชื่อนักศึกษา: ${studentData.S_name}`;
            setDialogMessage(message);
            setDialogOpen(true);
        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
            setDialogMessage('An error occurred while adding the student data.');
            setDialogOpen(true);
        }
    };

    const handleCloseDialog = () => {
        setDialogOpen(false);
    };

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <Box sx={{ marginLeft: 40 }}>
                                <Typography>
                                    <h1>เพิ่มรายชื่อนักศึกษา</h1>
                                </Typography>

                                {/* ฟิลด์สำหรับรหัสนักศึกษา */}
                                <Box sx={{ marginTop: 2 }}>
                                    <TextField
                                        label="รหัสนักศึกษา"
                                        value={studentID}
                                        onChange={(e) => setStudentID(e.target.value)}
                                        fullWidth
                                    />
                                </Box>

                                {/* ฟิลด์สำหรับชื่อนักศึกษา */}
                                <Box sx={{ marginTop: 2 }}>
                                    <TextField
                                        label="ชื่อนักศึกษา"
                                        value={studentName}
                                        onChange={(e) => setStudentName(e.target.value)}
                                        fullWidth
                                    />
                                </Box>

                                <Box>
                                    <Button sx={{ marginTop: 5, marginLeft: 10, marginRight: 10 }} variant="contained" onClick={handleConfirm}>
                                        Confirm
                                    </Button>
                                </Box>

                                <Dialog
                                    open={dialogOpen}
                                    onClose={handleCloseDialog}
                                    aria-labelledby="alert-dialog-title"
                                    aria-describedby="alert-dialog-description"
                                >
                                    <DialogActions>
                                        <Button onClick={handleCloseDialog} color="primary">
                                            Close
                                        </Button>
                                    </DialogActions>
                                </Dialog>
                            </Box>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
}

export default Addstudents;
