import React, { useState, useEffect } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Button, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import TextField from '@mui/material/TextField';

function SeparationProject() {
    const [projectData, setProjectData] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [open, setOpen] = useState(false);
    const [selectedProject, setSelectedProject] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:9999/Project');
                if (response.data && Array.isArray(response.data)) {
                    const uniqueProjects = Array.from(new Map(response.data.map(item => [item._id, item])).values());
                    setProjectData(uniqueProjects);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    // Filter projects based on the search term
    const filteredProjectData = projectData.filter(project => 
        project.P_name.toLowerCase().includes(searchTerm.toLowerCase()) || project._id.includes(searchTerm)
    );

    const remain = filteredProjectData.map(item => ({
        id: uuidv4(),
        ProjectID: item._id,
        name: item.P_name,
        members: `${item.P_S1}, ${item.P_S2}` // Assuming P_S1 and P_S2 are strings
    }));

    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleOpen = (project) => {
        setSelectedProject(project);
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
        setSelectedProject(null);
    };

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <div>
                                <h1>รายชื่อโครงงานทั้งหมด</h1>
                                <TextField
                                    label="ค้นหา"
                                    variant="outlined"
                                    style={{ marginBottom: '20px' }}
                                    value={searchTerm}
                                    onChange={handleSearch}
                                />
                                <p>โดย</p>
                                    <TextField
                                        label="ชื่อ-สกุลนักศึกษาคนที่ 1"
                                        value={projectDetails?.P_S1 || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                    />
                                    <TextField
                                        label="ชื่อ-สกุลนักศึกษาคนที่ 2"
                                        value={projectDetails?.P_S2 || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '25ch' } }}
                                    />
                                    <p>อาจารย์ที่ปรึกษา</p>
                                    <TextField
                                        label="ชื่ออาจารย์ที่ปรึกษา"
                                        value={projectDetails?.P_T || ''}
                                        disabled
                                        sx={{ '& > :not(style)': { mr: 5, ml: 5, width: '40ch' } }}
                                    />
                                <TableContainer>
                                    <Table>
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>ลำดับที่</TableCell>
                                                <TableCell>ชื่อโครงงาน</TableCell>
                                                <TableCell>สมาชิกโครงงาน</TableCell>
                                                <TableCell>แก้ไขสมาชิกโครงงาน</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {remain.length > 0 ? (
                                                remain.map((item, index) => (
                                                    <TableRow key={item.id}>
                                                        <TableCell>{index + 1}</TableCell>
                                                        <TableCell>{item.name}</TableCell>
                                                        <TableCell>{item.members}</TableCell>
                                                        <TableCell>
                                                            <Button 
                                                                variant="contained" 
                                                                color="primary" 
                                                                onClick={() => handleOpen(item)}
                                                            >
                                                                แก้ไขสมาชิกโครงงาน
                                                            </Button>
                                                        </TableCell>
                                                    </TableRow>
                                                ))
                                            ) : (
                                                <TableRow>
                                                    <TableCell colSpan={4}>No data available</TableCell>
                                                </TableRow>
                                            )}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                            </div>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>

            {/* Modal for displaying project details */}
            <Dialog open={open} onClose={handleClose}>
                <DialogTitle>รายละเอียดโครงงาน</DialogTitle>
                <DialogContent>
                    {selectedProject && (
                        <div>
                            <p><strong>ชื่อโครงงาน:</strong> {selectedProject.P_name}</p>
                            <p><strong>สมาชิกโครงงาน:</strong> {selectedProject.P_S1}, {selectedProject.P_S2}</p>
                        </div>
                    )}
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary">ปิด</Button>
                </DialogActions>
            </Dialog>
        </MainCard>
    );
}

export default SeparationProject;
