  const [project, setProject] = useState([]);
  const [Data, setData] = useState([
    { id: 1, name: 'การวิเคราะห์ปัญหา(Problem Analysis)', description: ['1. มีการระบุที่มาของปัญหา', '2. มีการนำเสนอข้อมูลทางสถิติหรือหลักฐานเพื่อสนับสนุนความสำคัญของปัญหา', '3. มีอ้างอิงแหล่งข้อมูลที่น่าเชื่อถือ'], score: '' },
    { id: 2, name: 'แนวคิด/ แนวทางของระบบ(Solution)', description: ['1. มีความสามารถแก้ปัญหาได้แท้จริง ตรงเป้า', '2. มีวิธีการที่นำเสนอที่มีประสิทธิภาพ'], score: '' },
    { id: 3, name: 'ขอบเขตและปริมาณงาน(Scope)', description: ['1. มีลักษณะและปริมาณงานเหมาะสมสำหรับปริญญานิพนธ์', '2. มีความเหมาะสมตามกรอบเวลา'], score: '' },
    { id: 4, name: 'ต้นแบบระบบ(System Prototype)', description: ['มีการนำเสนอต้นแบบระบบด้วยเครื่องมือที่มีความเหมาะสม เช่น Mockup, wireframe, story board และ system architecture'], score: '' },
    { id: 5, name: 'ลักษณะของกลุ่มผู้ใช้งาน(Target market)', description: ['1. มีการกำหนดเป้าหมายสำหรับการใช้งานระบบอย่างชัดเจน', '2. มีการศึกษาลักษณะกลุ่มเป้าหมาย', '3. มีการศึกษาผลกระทบที่คาดว่าจะเกิดต่อกลุ่มเป้าหมาย'], score: '' },
    { id: 6, name: 'การวิเคราะห์การตลาด (Market analysis)', description: ['1. มีการนำเสนอระบบที่ใกล้เคียง/ที่เกี่ยวข้องอย่างน้อย 2 ระบบ', '2. มีการระบุข้อจำกัดของระบบที่ใกล้เคียงหรือเกี่ยวข้อง', '3. มีการระบุความแตกต่างหรือจุดเด่นของระบบที่นำเสนอ'], score: '' },
    { id: 7, name: 'การศึกษาความเป็นไปได้ (Feasibility study)', description: ['1. มีการนำเสนอการศึกษาเทคนิคที่เกี่ยวข้องเบื้องต้น ', '2. มีการศึกษาข้อจำกัดของอุปกรณ์/ระบบที่เกี่ยวข้อง'], score: '' },
    { id: 8, name: 'ผลการประเมิน', score: '' },
  ]);

  const [ProjectSelect, setProjectSelect] = useState({
    ID: '',
    Name: '',
    SID: '',
    SName: '',
    SID2: '',
    SName2: '',
    TID: '',
    TName: '',
  });

  const [openPopup, setOpenPopup] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get('http://localhost:9999/Project');
        if (response.data && Array.isArray(response.data)) {
          setProject(response.data);
        }
      } catch (error) {
        console.error('Error fetching projects:', error);
      }
    };

    fetchProjects();
  }, []);

  const handleCheckboxChange = (id, type) => {
    setData(prevData =>
      prevData.map(item =>
        item.id === id
          ? { ...item, score: type === 'yes' ? 'ผ่าน' : 'ไม่ผ่าน' }
          : item
      )
    );
  };

  const updateFinalScore = () => {
    const countPass = Data
      .filter(item => item.id >= 1 && item.id <= 7)
      .filter(item => item.score === 'ผ่าน').length;

    const countFail = Data
      .filter(item => item.id >= 1 && item.id <= 7)
      .filter(item => item.score === 'ไม่ผ่าน').length;

    if (countFail >= 4) {
      setData(prevData =>
        prevData.map(item =>
          item.id === 8
            ? { ...item, score: 'ไม่ผ่านการประเมินหัวข้อ' }
            : item
        )
      );
    } else if (countPass >= 4) {
      setData(prevData =>
        prevData.map(item =>
          item.id === 8
            ? { ...item, score: 'ผ่านการประเมินหัวข้อ' }
            : item
        )
      );
    }
  };

  useEffect(() => {
    updateFinalScore();
  }, [Data]);

  const saveScore = async () => {
    const lastScore = Data[Data.length - 1].score;
    try {
      const response = await axios.post('http://localhost:9999/Exam_results', {
        Er_results: lastScore,
      });

      if (response.status === 200) {
        console.log('Score saved successfully!');
      } else {
        console.error('Failed to save score');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleLinkClick = (index) => {
    setOpenPopup(true);
    setSelectedRow(index);
  };

  const handleSavePopup = (e) => {
    e.preventDefault();
    handleSubmit(e);
    setOpenPopup(false);
    const updatedData = [...tableData];
    updatedData[selectedRow].isRed = false;
    updatedData[selectedRow].isDisabled = true;
    setTableData(updatedData);
    saveScore();
  };

  const handleClosePopup = () => {
    setOpenPopup(false);
  };

  const handleRedRow = (index) => {
    const updatedData = [...tableData];
    updatedData[index].isRed = true;
    updatedData[index].isDisabled = true;
    setTableData(updatedData);
  };

  const handleDisableRow = (index) => {
    const updatedData = [...tableData];
    updatedData[index].isDisabled = true;
    setTableData(updatedData);
  };

  const handleDonNotGoAny = () => {
    const updatedData = tableData.map(row => ({ ...row, isRed: true, isDisabled: true }));
    setTableData(updatedData);
  };

  const handleChange = (event) => {
    const selectedProjectName = event.target.value;
    const selectedProject = project.find((proj) => proj.Name === selectedProjectName);

    // Update the state with the selected project's details
    if (selectedProject) {
      setProjectSelect(selectedProject);
    } else {
      setProjectSelect({
        ID: '',
        Name: '',
        SID: '',
        SName: '',
        SID2: '',
        SName2: '',
        TID: '',
        TName: '',
      });
    }
  };

  return (
    <MainCard>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Grid container alignItems="center" justifyContent="space-between">
            <Grid item>
              <Grid container direction="column" spacing={1}></Grid>
              <div>
                <h1>ประเมินการสอบโครงงานพิเศษ</h1>
                <Button onClick={handleDonNotGoAny} variant="contained" color="error">ไม่เข้าร่วมประเมินทั้งหมด</Button>
                <Dialog open={openPopup} onClose={handleClosePopup} maxWidth='xl' fullWidth>
                  <DialogTitle>แบบประเมินการสอบโครงงานพิเศษ</DialogTitle>
                  <DialogContent sx={{ minWidth: 1000 }}>
                    {selectedRow !== null && (
                      <>
                        {tableData[selectedRow]?.status === 'CSB01' && (
                          <>
                            <div>
                              <Box fontSize="18px" sx={{ marginTop: 5 }}>
                                <h1>แบบประเมินหัวข้อโครงงานพิเศษ</h1>
                                {/* Select field for project name */}
                                <p>เลือกชื่อโครงงาน</p>
                                <FormControl>
                                  <InputLabel id="ProjectName-select-label">ชื่อโครงงาน</InputLabel>
                                  <Select
                                    labelId="ProjectName-select-label"
                                    value={ProjectSelect.Name || ''}
                                    onChange={handleChange}
                                  >
                                    {project.map((proj) => (
                                      <MenuItem key={proj.ID} value={proj.Name}>
                                        {proj.Name}
                                      </MenuItem>
                                    ))}
                                  </Select>
                                </FormControl>
                                <Stack spacing={2} sx={{ marginTop: 2 }}>
                                  {Data.map((item) => (
                                    <div key={item.id}>
                                      <h3>{item.name}</h3>
                                      {item.description.map((desc, index) => (
                                        <p key={index}>{desc}</p>
                                      ))}
                                      <div>
                                        <Button variant="contained" onClick={() => handleCheckboxChange(item.id, 'yes')}>ผ่าน</Button>
                                        <Button variant="contained" onClick={() => handleCheckboxChange(item.id, 'no')}>ไม่ผ่าน</Button>
                                      </div>
                                    </div>
                                  ))}
                                </Stack>
                              </Box>
                            </div>
                          </>
                        )}
                      </>
                    )}
                  </DialogContent>
                  <DialogActions>
                    <Button onClick={handleSavePopup} variant="contained" color="success">บันทึก</Button>
                    <Button onClick={handleClosePopup}>ปิด</Button>
                  </DialogActions>
                </Dialog>
              </div>
            </Grid>
          </Grid>
        </Grid>
        <Grid item xs={12}>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>ชื่อโครงงาน</TableCell>
                  <TableCell>ลิงค์</TableCell>
                  <TableCell>สถานะ</TableCell>
                  <TableCell>แก้ไข</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {tableData.map((row, index) => (
                  <TableRow key={row.id} style={{ backgroundColor: row.isRed ? 'red' : 'transparent' }}>
                    <TableCell>{row.name}</TableCell>
                    <TableCell>
                      <Button
                        disabled={row.isDisabled}
                        onClick={() => handleLinkClick(index)}
                        variant="outlined"
                      >
                        {row.link ? 'เปิดไฟล์' : 'ไม่มีลิงค์'}
                      </Button>
                    </TableCell>
                    <TableCell>{row.status}</TableCell>
                    <TableCell>
                      <Button
                        disabled={row.isDisabled}
                        onClick={() => handleRedRow(index)}
                        color="error"
                      >
                        ทำให้แถบสีแดง
                      </Button>
                      <Button
                        disabled={row.isDisabled}
                        onClick={() => handleDisableRow(index)}
                        color="warning"
                      >
                        ปิดใช้งาน
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Grid>
      </Grid>
    </MainCard>
  );
}

export default InputScoreCSB01;
