import React, { useState, useEffect } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography } from '@mui/material';
import { Grid } from '@mui/material';
import MainCard from 'ui-component/cards/MainCard';
import { gridSpacing } from 'store/constant';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import TextField from '@mui/material/TextField';

function SeparationProject() {
    const [projectData, setProjectData] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:9999/projects'); // Update the endpoint for projects
                if (response.data && Array.isArray(response.data)) {
                    const uniqueProjects = Array.from(new Map(response.data.map(item => [item._id, item])).values());
                    setProjectData(uniqueProjects);
                }
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    // Filter projects based on the search term
    const filteredProjectData = projectData.filter(project => 
        project.P_name.toLowerCase().includes(searchTerm.toLowerCase()) || project.P_id.includes(searchTerm)
    );

    const remain = filteredProjectData.map(item => ({
        id: uuidv4(),
        ProjectID: item._id,
        name: item.ญ_name,
    }));

    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
    };

    return (
        <MainCard>
            <Grid container spacing={gridSpacing}>
                <Grid item xs={12}>
                    <Grid container alignItems="center" justifyContent="space-between">
                        <Grid item>
                            <div>
                                <h1>รายชื่อโครงงานทั้งหมด</h1> {/* Updated heading */}
                                <TextField
                                    label="ค้นหา"
                                    variant="outlined"
                                    style={{ marginBottom: '20px' }}
                                    value={searchTerm}
                                    onChange={handleSearch}
                                />
                                <TableContainer>
                                    <Table>
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>ลำดับที่</TableCell>
                                                <TableCell>รหัสโครงงาน</TableCell>
                                                <TableCell>ชื่อโครงงาน</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {remain.length > 0 ? (
                                                remain.map((item, index) => (
                                                    <TableRow key={item.id}>
                                                        <TableCell>{index + 1}</TableCell>
                                                        <TableCell>{item.ProjectID}</TableCell>
                                                        <TableCell>{item.name}</TableCell>
                                                    </TableRow>
                                                ))
                                            ) : (
                                                <TableRow>
                                                    <TableCell colSpan={3}>No data available</TableCell>
                                                </TableRow>
                                            )}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                            </div>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </MainCard>
    );
}

export default SeparationProject;
